00149     public void DeletionPermittedRegister(SimplePath simplePath)
00150     {
00151         lock (_pathLock)
00152         {
00153             var absolutePath = simplePath.AbsolutePath;
00154 
00155             if (absolutePath == "/" || absolutePath == "\\" || string.IsNullOrWhiteSpace(absolutePath))
00156                 return;
00157 
00158             if (PermittanceChecker.IsRootOrHomeDirectory(simplePath, this))
00159                 return;
00160 
00161             DeletionPermittedPathList = DeletionPermittedPathList.Add(simplePath);
00162         }
00163     }
00164 
00165     public void DeletionPermittedDispose(SimplePath simplePath)
00166     {
00167         lock (_pathLock)
00168         {
00169             DeletionPermittedPathList = DeletionPermittedPathList.Remove(simplePath);
00170         }
00171     }
