00065     protected override void OnInitialized()
00066     {
00067         BackgroundTaskService.Enqueue(
00068             Key<IBackgroundTask>.NewKey(),
00069             ContinuousBackgroundTaskWorker.GetQueueKey(),
00070             nameof(LuthetusIdeInitializer),
00071             async () =>
00072             {
00073                 if (TextEditorConfig.CustomThemeRecordList is not null)
00074                 {
00075                     foreach (var themeRecord in TextEditorConfig.CustomThemeRecordList)
00076                     {
00077                         Dispatcher.Dispatch(new ThemeState.RegisterAction(themeRecord));
00078                     }
00079                 }
00080 
00081                 foreach (var terminalKey in TerminalFacts.WELL_KNOWN_KEYS)
00082                 {
00083                     if (terminalKey == TerminalFacts.GENERAL_KEY)
00084                         AddGeneralTerminal();
00085                     else if (terminalKey == TerminalFacts.EXECUTION_KEY)
00086                         AddExecutionTerminal();
00087                 }
00088                 
00089                 Dispatcher.Dispatch(new CodeSearchState.InitializeResizeHandleDimensionUnitAction(
00090                     new DimensionUnit
00091                     {
00092                         ValueFunc = () => AppOptionsStateWrap.Value.Options.ResizeHandleHeightInPixels / 2,
00093                         DimensionUnitKind = DimensionUnitKind.Pixels,
00094                         DimensionOperatorKind = DimensionOperatorKind.Subtract,
00095                         Purpose = DimensionUnitFacts.Purposes.RESIZABLE_HANDLE_ROW,
00096                     }));
00097 
00098                 InitializePanelResizeHandleDimensionUnit();
00099                 InitializePanelTabs();
00100                 CommandFactory.Initialize();
00101             });
00102             
00103         base.OnInitialized();
00104     }
00105     
00106     protected override async Task OnAfterRenderAsync(bool firstRender)
00107     {
00108         if (firstRender)
00109         {
00110             if (LuthetusHostingInformation.LuthetusHostingKind == LuthetusHostingKind.Photino)
00111             {
00112                 await JsRuntime.GetLuthetusIdeApi()
00113                     .PreventDefaultBrowserKeybindings();
00114             }
00115         }
00116         
00117         await base.OnAfterRenderAsync(firstRender);
00118     }
