00253     private void AddGeneralTerminal()
00254     {
00255         if (LuthetusHostingInformation.LuthetusHostingKind == LuthetusHostingKind.Wasm ||
00256             LuthetusHostingInformation.LuthetusHostingKind == LuthetusHostingKind.ServerSide)
00257         {
00258             Dispatcher.Dispatch(new TerminalState.RegisterAction(
00259                 new TerminalWebsite(
00260                     "General",
00261                     terminal => new TerminalInteractive(terminal),
00262                     terminal => new TerminalInputStringBuilder(terminal),
00263                     terminal => new TerminalOutput(
00264                         terminal,
00265                         new TerminalOutputFormatterExpand(
00266                             terminal,
00267                             TextEditorService,
00268                             CompilerServiceRegistry,
00269                             DialogService,
00270                                JsRuntime,
00271                             Dispatcher)),
00272                     BackgroundTaskService,
00273                     CommonComponentRenderers,
00274                     Dispatcher)
00275                 {
00276                     Key = TerminalFacts.GENERAL_KEY
00277                 }));
00278         }
00279         else
00280         {
00281             Dispatcher.Dispatch(new TerminalState.RegisterAction(
00282                 new Terminal(
00283                     "General",
00284                     terminal => new TerminalInteractive(terminal),
00285                     terminal => new TerminalInputStringBuilder(terminal),
00286                     terminal => new TerminalOutput(
00287                         terminal,
00288                         new TerminalOutputFormatterExpand(
00289                             terminal,
00290                             TextEditorService,
00291                             CompilerServiceRegistry,
00292                             DialogService,
00293                             JsRuntime,
00294                             Dispatcher)),
00295                     BackgroundTaskService,
00296                     CommonComponentRenderers,
00297                     Dispatcher)
00298                 {
00299                     Key = TerminalFacts.GENERAL_KEY
00300                 }));
00301         }
00302     }
