00155     private async Task ParseSolutionAsync()
00156     {
00157         var allFiles = new List<string>();
00158 
00159         await RecursiveStep(
00160                 new List<string> { "/" },
00161                 allFiles)
00162             .ConfigureAwait(false);
00163 
00164         async Task RecursiveStep(IEnumerable<string> directories, List<string> allFiles)
00165         {
00166             foreach (var directory in directories)
00167             {
00168                 var childDirectories = await FileSystemProvider.Directory
00169                     .GetDirectoriesAsync(directory)
00170                     .ConfigureAwait(false);
00171 
00172                 allFiles.AddRange(
00173                     await FileSystemProvider.Directory.GetFilesAsync(directory).ConfigureAwait(false));
00174 
00175                 await RecursiveStep(childDirectories, allFiles).ConfigureAwait(false);
00176             }
00177         }
00178 
00179         foreach (var file in allFiles)
00180         {
00181             var absolutePath = EnvironmentProvider.AbsolutePathFactory(file, false);
00182             var resourceUri = new ResourceUri(file);
00183             var fileLastWriteTime = await FileSystemProvider.File.GetLastWriteTimeAsync(file).ConfigureAwait(false);
00184             var content = await FileSystemProvider.File.ReadAllTextAsync(file).ConfigureAwait(false);
00185             
00186             var decorationMapper = DecorationMapperRegistry.GetDecorationMapper(absolutePath.ExtensionNoPeriod);
00187             var compilerService = CompilerServiceRegistry.GetCompilerService(absolutePath.ExtensionNoPeriod);
00188 
00189             var textEditorModel = new TextEditorModel(
00190                 resourceUri,
00191                 fileLastWriteTime,
00192                 absolutePath.ExtensionNoPeriod,
00193                 content,
00194                 decorationMapper,
00195                 compilerService);
00196 
00197             TextEditorService.ModelApi.RegisterCustom(textEditorModel);
00198 
00199             TextEditorService.PostUnique(
00200                 nameof(TextEditorService.ModelApi.AddPresentationModel),
00201                 editContext =>
00202                 {
00203                     var modelModifier = editContext.GetModelModifier(textEditorModel.ResourceUri);
00204 
00205                     if (modelModifier is null)
00206                         return Task.CompletedTask;
00207 
00208                     TextEditorService.ModelApi.AddPresentationModel(
00209                         editContext,
00210                         modelModifier,
00211                         CompilerServiceDiagnosticPresentationFacts.EmptyPresentationModel);
00212                     
00213                     TextEditorService.ModelApi.AddPresentationModel(
00214                         editContext,
00215                         modelModifier,
00216                         FindOverlayPresentationFacts.EmptyPresentationModel);
00217 
00218                     TextEditorService.ModelApi.AddPresentationModel(
00219                         editContext,
00220                         modelModifier,
00221                         DiffPresentationFacts.EmptyInPresentationModel);
00222 
00223                     TextEditorService.ModelApi.AddPresentationModel(
00224                         editContext,
00225                         modelModifier,
00226                         DiffPresentationFacts.EmptyOutPresentationModel);
00227 
00228                     textEditorModel.CompilerService.RegisterResource(
00229                         textEditorModel.ResourceUri,
00230                         shouldTriggerResourceWasModified: true);
00231                     return Task.CompletedTask;
00232                 });
00233         }
00234     }
