00188     private void PerformNewFile(
00189         string fileName,
00190         IFileTemplate? exactMatchFileTemplate,
00191         ImmutableArray<IFileTemplate> relatedMatchFileTemplatesList,
00192         NamespacePath namespacePath,
00193         Func<Task> onAfterCompletion)
00194     {
00195         _backgroundTaskService.Enqueue(
00196             Key<IBackgroundTask>.NewKey(),
00197             ContinuousBackgroundTaskWorker.GetQueueKey(),
00198             "New File Action",
00199             async () =>
00200             {
00201                 if (exactMatchFileTemplate is null)
00202                 {
00203                     var emptyFileAbsolutePathString = namespacePath.AbsolutePath.Value + fileName;
00204 
00205                     var emptyFileAbsolutePath = _environmentProvider.AbsolutePathFactory(
00206                         emptyFileAbsolutePathString,
00207                         false);
00208 
00209                     await _fileSystemProvider.File.WriteAllTextAsync(
00210                             emptyFileAbsolutePath.Value,
00211                             string.Empty,
00212                             CancellationToken.None)
00213                         .ConfigureAwait(false);
00214                 }
00215                 else
00216                 {
00217                     var allTemplates = new[] { exactMatchFileTemplate }
00218                         .Union(relatedMatchFileTemplatesList)
00219                         .ToArray();
00220 
00221                     foreach (var fileTemplate in allTemplates)
00222                     {
00223                         var templateResult = fileTemplate.ConstructFileContents.Invoke(
00224                             new FileTemplateParameter(fileName, namespacePath, _environmentProvider));
00225 
00226                         await _fileSystemProvider.File.WriteAllTextAsync(
00227                                 templateResult.FileNamespacePath.AbsolutePath.Value,
00228                                 templateResult.Contents,
00229                                 CancellationToken.None)
00230                             .ConfigureAwait(false);
00231                     }
00232                 }
00233 
00234                 await onAfterCompletion.Invoke().ConfigureAwait(false);
00235             });
00236     }
