00321     private void PerformPasteFile(IAbsolutePath receivingDirectory, Func<Task> onAfterCompletion)
00322     {
00323         _backgroundTaskService.Enqueue(
00324             Key<IBackgroundTask>.NewKey(),
00325             ContinuousBackgroundTaskWorker.GetQueueKey(),
00326             "Paste File Action",
00327             async () =>
00328             {
00329                 var clipboardContents = await _clipboardService.ReadClipboard().ConfigureAwait(false);
00330 
00331                 if (ClipboardFacts.TryParseString(clipboardContents, out var clipboardPhrase))
00332                 {
00333                     if (clipboardPhrase is not null &&
00334                         clipboardPhrase.DataType == ClipboardFacts.AbsolutePathDataType)
00335                     {
00336                         if (clipboardPhrase.Command == ClipboardFacts.CopyCommand ||
00337                             clipboardPhrase.Command == ClipboardFacts.CutCommand)
00338                         {
00339                             IAbsolutePath? clipboardAbsolutePath = null;
00340 
00341                             // Should the if and else if be kept as inline awaits?
00342                             // If kept as inline awaits then the else if won't execute if the first one succeeds.
00343                             if (await _fileSystemProvider.Directory.ExistsAsync(clipboardPhrase.Value).ConfigureAwait(false))
00344                             {
00345                                 clipboardAbsolutePath = _environmentProvider.AbsolutePathFactory(
00346                                     clipboardPhrase.Value,
00347                                     true);
00348                             }
00349                             else if (await _fileSystemProvider.File.ExistsAsync(clipboardPhrase.Value).ConfigureAwait(false))
00350                             {
00351                                 clipboardAbsolutePath = _environmentProvider.AbsolutePathFactory(
00352                                     clipboardPhrase.Value,
00353                                     false);
00354                             }
00355 
00356                             if (clipboardAbsolutePath is not null)
00357                             {
00358                                 var successfullyPasted = true;
00359 
00360                                 try
00361                                 {
00362                                     if (clipboardAbsolutePath.IsDirectory)
00363                                     {
00364                                         var clipboardDirectoryInfo = new DirectoryInfo(clipboardAbsolutePath.Value);
00365                                         var receivingDirectoryInfo = new DirectoryInfo(receivingDirectory.Value);
00366 
00367                                         CopyFilesRecursively(clipboardDirectoryInfo, receivingDirectoryInfo);
00368                                     }
00369                                     else
00370                                     {
00371                                         var destinationAbsolutePathString = receivingDirectory.Value +
00372                                             clipboardAbsolutePath.NameWithExtension;
00373 
00374                                         var sourceAbsolutePathString = clipboardAbsolutePath.Value;
00375 
00376                                         await _fileSystemProvider.File.CopyAsync(
00377                                                 sourceAbsolutePathString,
00378                                                 destinationAbsolutePathString)
00379                                             .ConfigureAwait(false);
00380                                     }
00381                                 }
00382                                 catch (Exception)
00383                                 {
00384                                     successfullyPasted = false;
00385                                 }
00386 
00387                                 if (successfullyPasted && clipboardPhrase.Command == ClipboardFacts.CutCommand)
00388                                 {
00389                                     // TODO: Rerender the parent of the deleted due to cut file
00390                                     PerformDeleteFile(clipboardAbsolutePath, onAfterCompletion);
00391                                 }
00392                                 else
00393                                 {
00394                                     await onAfterCompletion.Invoke().ConfigureAwait(false);
00395                                 }
00396                             }
00397                         }
00398                     }
00399                 }
00400             });
00401     }
