00403     private IAbsolutePath? PerformRename(IAbsolutePath sourceAbsolutePath, string nextName, IDispatcher dispatcher, Func<Task> onAfterCompletion)
00404     {
00405         // Check if the current and next name match when compared with case insensitivity
00406         if (0 == string.Compare(sourceAbsolutePath.NameWithExtension, nextName, StringComparison.OrdinalIgnoreCase))
00407         {
00408             var temporaryNextName = _environmentProvider.GetRandomFileName();
00409 
00410             var temporaryRenameResult = PerformRename(
00411                 sourceAbsolutePath,
00412                 temporaryNextName,
00413                 dispatcher,
00414                 () => Task.CompletedTask);
00415 
00416             if (temporaryRenameResult is null)
00417             {
00418                 onAfterCompletion.Invoke();
00419                 return null;
00420             }
00421             else
00422             {
00423                 sourceAbsolutePath = temporaryRenameResult;
00424             }
00425         }
00426 
00427         var sourceAbsolutePathString = sourceAbsolutePath.Value;
00428         var parentOfSource = sourceAbsolutePath.AncestorDirectoryList.Last().Value;
00429         var destinationAbsolutePathString = parentOfSource + nextName;
00430 
00431         try
00432         {
00433             if (sourceAbsolutePath.IsDirectory)
00434                 _fileSystemProvider.Directory.MoveAsync(sourceAbsolutePathString, destinationAbsolutePathString);
00435             else
00436                 _fileSystemProvider.File.MoveAsync(sourceAbsolutePathString, destinationAbsolutePathString);
00437         }
00438         catch (Exception e)
00439         {
00440             NotificationHelper.DispatchError("Rename Action", e.Message, _commonComponentRenderers, dispatcher, TimeSpan.FromSeconds(14));
00441             onAfterCompletion.Invoke();
00442             return null;
00443         }
00444 
00445         onAfterCompletion.Invoke();
00446 
00447         return _environmentProvider.AbsolutePathFactory(destinationAbsolutePathString, sourceAbsolutePath.IsDirectory);
00448     }
