00454     public static DirectoryInfo CopyFilesRecursively(DirectoryInfo source, DirectoryInfo target)
00455     {
00456         var newDirectoryInfo = target.CreateSubdirectory(source.Name);
00457         foreach (var fileInfo in source.GetFiles())
00458             fileInfo.CopyTo(Path.Combine(newDirectoryInfo.FullName, fileInfo.Name));
00459 
00460         foreach (var childDirectoryInfo in source.GetDirectories())
00461             CopyFilesRecursively(childDirectoryInfo, newDirectoryInfo);
00462 
00463         return newDirectoryInfo;
00464     }
