00194             {
00195                 var currentRichCharacter = modelModifier.RichCharacterList[positionIndex];
00196                 
00197                 var currentCharacterKind = CharacterKindHelper.CharToCharacterKind(
00198                     currentRichCharacter.Value);
00199                 
00200                 if (currentCharacterKind != CharacterKind.LetterOrDigit)
00201                     break;
00202                 if (Char.IsUpper(currentRichCharacter.Value))
00203                     break;
00204             }
00205             
00206             var columnDisplacement = positionIndex - rememberStartPositionIndex;
00207             
