00231     [Fact]
00232     public void Home_THEN_MoreFunctionality()
00233     {
00234         var initialContent = @" AppleBananaCucumber";
00235 
00236         var model = new TextEditorModel(
00237             new ResourceUri("/unitTesting.cs"),
00238             DateTime.UtcNow,
00239             ExtensionNoPeriodFacts.C_SHARP_CLASS,
00240             initialContent,
00241             null,
00242             null);
00243             
00244         Assert.Equal(initialContent, model.GetAllText());
00245             
00246         var modelModifier = new TextEditorModelModifier(model);
00247         
00248         var lineInformation = modelModifier.GetLineInformation(0);
00249         var cursor = new TextEditorCursor(
00250             lineIndex: 0,
00251             columnIndex: lineInformation.LastValidColumnIndex,
00252             isPrimaryCursor: true);
00253             
00254         Assert.Equal(0, cursor.LineIndex);
00255         Assert.Equal(lineInformation.LastValidColumnIndex, cursor.ColumnIndex);
00256         Assert.Equal(lineInformation.LastValidColumnIndex, cursor.PreferredColumnIndex);
00257         
00258         KeybindUnderTest();
00259 
00260         Assert.Equal(0, cursor.LineIndex);
00261         Assert.Equal(1, cursor.ColumnIndex);
00262         Assert.Equal(1, cursor.PreferredColumnIndex);
00263         
00264         throw new NotImplementedException("TODO: Handle edge cases");
00265         
00266         // Going to write the keybind here then move this code when done
00267         void KeybindUnderTest()
00268         {
00269             var cursorModifier = new TextEditorCursorModifier(cursor);
00270             var originalPositionIndex = modelModifier.GetPositionIndex(cursorModifier);
00271             
00272             cursorModifier.ColumnIndex = 0;
00273             
00274             var lineInformation = modelModifier.GetLineInformation(cursorModifier.LineIndex);
00275             var indentationPositionIndex = modelModifier.GetPositionIndex(cursorModifier);
00276             
00277             var cursorWithinIndentation = false;
00278 
00279             while (indentationPositionIndex < lineInformation.LastValidColumnIndex)
00280             {
00281                 var possibleIndentationChar = modelModifier.RichCharacterList[indentationPositionIndex++].Value;
00282 
00283                 if (possibleIndentationChar == '\t' || possibleIndentationChar == ' ')
00284                 {
00285                     if (indentationPositionIndex == originalPositionIndex)
00286                     {
00287                         cursorWithinIndentation = true;
00288                         break;
00289                     }
00290                 }
00291                 else
00292                 {
00293                     // Eager incrementation is making this too large by 1
00294                     indentationPositionIndex--;
00295                     break;
00296                 }
00297             }
00298             
00299             if (originalPositionIndex == 0)
00300             {
00301                 var exclusiveEndIndentationPositionIndex = indentationPositionIndex;
00302                 cursorModifier.SetColumnIndexAndPreferred(exclusiveEndIndentationPositionIndex);
00303             }
00304             else if (!cursorWithinIndentation)
00305             {
00306                 var exclusiveEndIndentationPositionIndex = indentationPositionIndex;
00307                 cursorModifier.SetColumnIndexAndPreferred(exclusiveEndIndentationPositionIndex);
00308             }
00309             // else: Stay at column 0
00310             
00311             cursor = cursorModifier.ToCursor();
00312         }
00313     }
