00729                 dispatcher.Dispatch(new NotificationState.MakeArchivedAction(sampleNotificationRecord.DynamicViewModelKey));
00730                 Assert.Equal(3, notificationStateWrap.Value.ArchivedList.Count);
00731                 Assert.Contains(notificationStateWrap.Value.ArchivedList, x => x == sampleNotificationRecord);
00732             }
00733 
00734             dispatcher.Dispatch(new NotificationState.ClearArchivedAction());
00735             Assert.Empty(notificationStateWrap.Value.ArchivedList);
00736         }
00737     }
