00084     public void AddToBatch(KeymapArgs keymapArgs)
00085     {
00086         if (!BatchHasAvailability)
00087             throw new LuthetusTextEditorException($"{nameof(BatchLength)} >= {nameof(MAX_BATCH_SIZE)}");
00088 
00089         KeymapArgsList[BatchLength] = keymapArgs;
00090         BatchLength++;
00091     }
00092 
00093     public IBackgroundTask? BatchOrDefault(IBackgroundTask upstreamEvent)
00094     {
00095         if (upstreamEvent is OnKeyDownLateBatching upstreamOnKeyDownLateBatching)
00096         {
00097             if (BatchLength == 1 && upstreamOnKeyDownLateBatching.BatchHasAvailability)
00098                 upstreamOnKeyDownLateBatching.AddToBatch(KeymapArgsList[0]);
00099 
00100             return upstreamOnKeyDownLateBatching;
00101         }
00102 
00103         return null;
00104     }
