00404     private bool KeyAndModifiersAreEqual(KeymapArgs x, KeymapArgs y)
00405     {
00406         return
00407             x.Key == y.Key &&
00408             x.ShiftKey == y.ShiftKey &&
00409             x.CtrlKey == y.CtrlKey &&
00410             x.AltKey == y.AltKey;
00411     }
