00033     public IBackgroundTask? BatchOrDefault(IBackgroundTask oldEvent)
00034     {
00035         // Horizontal mouse wheel was not working on Linux.
00036         // Prior to the fix that was just made, only the 'WheelEventArgs.DeltaY'
00037         // was being used.
00038         //
00039         // Reason being, on Windows, regardless of if 'WheelEventArgs.ShiftKey' was true or false,
00040         // the 'WheelEventArgs.DeltaY' was always being provided as a non-zero value.
00041         //
00042         // But, on Linux, when not holding the shift key: 'WheelEventArgs.DeltaY' is set,
00043         // and 'WheelEventArgs.DeltaX' is 0.
00044         // The vice versa though, if one does hold the shift key, 'WheelEventArgs.DeltaY' is zero,
00045         // and 'WheelEventArgs.DeltaX' is set.
00046         //
00047         // If I end up breaking Windows with this change (to use ether 'WheelEventArgs.DeltaY'
00048         // or 'WheelEventArgs.DeltaX' dependent on 'WheelEventArgs.ShiftKey',
00049         // then... I'm tired and forgot what I was typing. (2024-05-28)
00050 
00051 
00052         // If the two individuals, or a batch and an individual are both positive,
00053         // then batch them, etc... for negative and 0
00054 
00055         if (oldEvent is OnWheel oldEventOnWheel)
00056         {
00057             if (oldEventOnWheel.WheelEventArgs.ShiftKey && WheelEventArgs.ShiftKey)
00058             {
00059                 if (oldEventOnWheel.WheelEventArgs.DeltaX > 0 &&
00060                     WheelEventArgs.DeltaX > 0)
00061                 {
00062                     return new OnWheelBatch(
00063                         new List<WheelEventArgs>()
00064                         {
00065                             oldEventOnWheel.WheelEventArgs,
00066                             WheelEventArgs
00067                         },
00068                         ComponentData,
00069                         ViewModelKey)
00070                         {
00071                             EditContext = EditContext
00072                         };
00073                 }
00074                 else if (oldEventOnWheel.WheelEventArgs.DeltaX < 0 &&
00075                          WheelEventArgs.DeltaX < 0)
00076                 {
00077                     return new OnWheelBatch(
00078                         new List<WheelEventArgs>()
00079                         {
00080                             oldEventOnWheel.WheelEventArgs,
00081                             WheelEventArgs
00082                         },
00083                         ComponentData,
00084                         ViewModelKey)
00085                         {
00086                             EditContext = EditContext
00087                         };
00088                 }
00089                 else if (oldEventOnWheel.WheelEventArgs.DeltaX == 0 &&
00090                          WheelEventArgs.DeltaX == 0)
00091                 {
00092                     return new OnWheelBatch(
00093                         new List<WheelEventArgs>()
00094                         {
00095                             oldEventOnWheel.WheelEventArgs,
00096                             WheelEventArgs
00097                         },
00098                         ComponentData,
00099                         ViewModelKey)
00100                         {
00101                             EditContext = EditContext
00102                         };
00103                 }
00104             }
00105             else if (!oldEventOnWheel.WheelEventArgs.ShiftKey && !WheelEventArgs.ShiftKey)
00106             {
00107                 if (oldEventOnWheel.WheelEventArgs.DeltaY > 0 &&
00108                     WheelEventArgs.DeltaY > 0)
00109                 {
00110                     return new OnWheelBatch(
00111                         new List<WheelEventArgs>()
00112                         {
00113                             oldEventOnWheel.WheelEventArgs,
00114                             WheelEventArgs
00115                         },
00116                         ComponentData,
00117                         ViewModelKey)
00118                         {
00119                             EditContext = EditContext
00120                         };
00121                 }
00122                 else if (oldEventOnWheel.WheelEventArgs.DeltaY < 0 &&
00123                          WheelEventArgs.DeltaY < 0)
00124                 {
00125                     return new OnWheelBatch(
00126                         new List<WheelEventArgs>()
00127                         {
00128                             oldEventOnWheel.WheelEventArgs,
00129                             WheelEventArgs
00130                         },
00131                         ComponentData,
00132                         ViewModelKey)
00133                         {
00134                             EditContext = EditContext
00135                         };
00136                 }
00137                 else if (oldEventOnWheel.WheelEventArgs.DeltaY == 0 &&
00138                          WheelEventArgs.DeltaY == 0)
00139                 {
00140                     return new OnWheelBatch(
00141                         new List<WheelEventArgs>()
00142                         {
00143                             oldEventOnWheel.WheelEventArgs,
00144                             WheelEventArgs
00145                         },
00146                         ComponentData,
00147                         ViewModelKey)
00148                         {
00149                             EditContext = EditContext
00150                         };
00151                 }
00152             }
00153         }
00154 
00155         if (oldEvent is OnWheelBatch oldEventOnWheelBatch)
00156         {
00157             if (oldEventOnWheelBatch.WheelEventArgsList.Last().ShiftKey && WheelEventArgs.ShiftKey)
00158             {
00159                 if (oldEventOnWheelBatch.WheelEventArgsList.Last().DeltaX > 0 &&
00160                 WheelEventArgs.DeltaX > 0)
00161                 {
00162                     oldEventOnWheelBatch.WheelEventArgsList.Add(WheelEventArgs);
00163                     return oldEventOnWheelBatch;
00164                 }
00165                 else if (oldEventOnWheelBatch.WheelEventArgsList.Last().DeltaX < 0 &&
00166                          WheelEventArgs.DeltaX < 0)
00167                 {
00168                     oldEventOnWheelBatch.WheelEventArgsList.Add(WheelEventArgs);
00169                     return oldEventOnWheelBatch;
00170                 }
00171                 else if (oldEventOnWheelBatch.WheelEventArgsList.Last().DeltaX == 0 &&
00172                          WheelEventArgs.DeltaX == 0)
00173                 {
00174                     oldEventOnWheelBatch.WheelEventArgsList.Add(WheelEventArgs);
00175                     return oldEventOnWheelBatch;
00176                 }
00177             }
00178          else if (!oldEventOnWheelBatch.WheelEventArgsList.Last().ShiftKey && !WheelEventArgs.ShiftKey)
00179             {
00180                 if (oldEventOnWheelBatch.WheelEventArgsList.Last().DeltaY > 0 &&
00181                 WheelEventArgs.DeltaY > 0)
00182                 {
00183                     oldEventOnWheelBatch.WheelEventArgsList.Add(WheelEventArgs);
00184                     return oldEventOnWheelBatch;
00185                 }
00186                 else if (oldEventOnWheelBatch.WheelEventArgsList.Last().DeltaY < 0 &&
00187                          WheelEventArgs.DeltaY < 0)
00188                 {
00189                     oldEventOnWheelBatch.WheelEventArgsList.Add(WheelEventArgs);
00190                     return oldEventOnWheelBatch;
00191                 }
00192                 else if (oldEventOnWheelBatch.WheelEventArgsList.Last().DeltaY == 0 &&
00193                          WheelEventArgs.DeltaY == 0)
00194                 {
00195                     oldEventOnWheelBatch.WheelEventArgsList.Add(WheelEventArgs);
00196                     return oldEventOnWheelBatch;
00197                 }
00198             }
00199         }
00200 
00201         return null;
00202     }
