00204     public async Task HandleEvent(CancellationToken cancellationToken)
00205     {
00206         try
00207         {
00208             var viewModelModifier = EditContext.GetViewModelModifier(ViewModelKey);
00209             if (viewModelModifier is null)
00210                 return;
00211 
00212             // TODO: Why was this made as 'if' 'else' whereas the OnWheelBatch...
00213             //       ...is doing 'if' 'if'.
00214             //       |
00215             //       The OnWheelBatch doesn't currently batch horizontal with vertical
00216             //       the OnWheel events have to be the same axis to batch.
00217             if (WheelEventArgs.ShiftKey)
00218             {
00219                 EditContext.TextEditorService.ViewModelApi.MutateScrollHorizontalPosition(
00220                     EditContext,
00221                     viewModelModifier,
00222                     WheelEventArgs.DeltaX);
00223             }
00224             else
00225             {
00226                 EditContext.TextEditorService.ViewModelApi.MutateScrollVerticalPosition(
00227                     EditContext,
00228                     viewModelModifier,
00229                     WheelEventArgs.DeltaY);
00230             }
00231             
00232             await EditContext.TextEditorService
00233                 .FinalizePost(EditContext)
00234                 .ConfigureAwait(false);
00235                 
00236             await Task.Delay(ThrottleFacts.TwentyFour_Frames_Per_Second).ConfigureAwait(false);
00237         }
00238         catch (Exception e)
00239         {
00240             Console.WriteLine(e);
00241         }
00242     }
