00304     private void InitializeOptionsApiTests(
00305         out ITextEditorService textEditorService,
00306         out IDispatcher dispatcher,
00307         out ServiceProvider serviceProvider)
00308     {
00309         var backgroundTaskService = new BackgroundTaskServiceSynchronous();
00310 
00311         var serviceCollection = new ServiceCollection()
00312             .AddScoped<IJSRuntime, DoNothingJsRuntime>()
00313             .AddLuthetusTextEditor(new LuthetusHostingInformation(
00314                 LuthetusHostingKind.UnitTestingSynchronous,
00315                 LuthetusPurposeKind.TextEditor,
00316                 backgroundTaskService))
00317             .AddFluxor(options => options.ScanAssemblies(
00318                 typeof(LuthetusCommonConfig).Assembly,
00319                 typeof(LuthetusTextEditorConfig).Assembly));
00320 
00321         serviceProvider = serviceCollection.BuildServiceProvider();
00322 
00323         var store = serviceProvider.GetRequiredService<IStore>();
00324         store.InitializeAsync().Wait();
00325 
00326         dispatcher = serviceProvider.GetRequiredService<IDispatcher>();
00327         textEditorService = serviceProvider.GetRequiredService<ITextEditorService>();
00328     }
