00099     public async Task OnDragStartAsync()
00100     {
00101         var dropzoneList = new List<IDropzone>();
00102         AddFallbackDropzone(dropzoneList);
00103 
00104         var panelGroupHtmlIdTupleList = new (Key<PanelGroup> PanelGroupKey, string HtmlElementId)[]
00105         {
00106             (PanelFacts.LeftPanelGroupKey, "luth_ide_panel_left_tabs"),
00107             (PanelFacts.RightPanelGroupKey, "luth_ide_panel_right_tabs"),
00108             (PanelFacts.BottomPanelGroupKey, "luth_ide_panel_bottom_tabs"),
00109         };
00110 
00111         foreach (var panelGroupHtmlIdTuple in panelGroupHtmlIdTupleList)
00112         {
00113             var measuredHtmlElementDimensions = await JsRuntime.GetLuthetusCommonApi()
00114                 .MeasureElementById(panelGroupHtmlIdTuple.HtmlElementId)
00115                 .ConfigureAwait(false);
00116 
00117             measuredHtmlElementDimensions = measuredHtmlElementDimensions with
00118             {
00119                 ZIndex = 1,
00120             };
00121 
00122             var elementDimensions = new ElementDimensions();
00123 
00124             elementDimensions.ElementPositionKind = ElementPositionKind.Fixed;
00125 
00126             // Width
00127             {
00128                 var widthDimensionAttribute = elementDimensions.DimensionAttributeList.First(
00129                     x => x.DimensionAttributeKind == DimensionAttributeKind.Width);
00130 
00131                 widthDimensionAttribute.DimensionUnitList.Clear();
00132                 widthDimensionAttribute.DimensionUnitList.Add(new DimensionUnit
00133                 {
00134                     Value = measuredHtmlElementDimensions.WidthInPixels,
00135                     DimensionUnitKind = DimensionUnitKind.Pixels
00136                 });
00137             }
00138 
00139             // Height
00140             {
00141                 var heightDimensionAttribute = elementDimensions.DimensionAttributeList.First(
00142                     x => x.DimensionAttributeKind == DimensionAttributeKind.Height);
00143 
00144                 heightDimensionAttribute.DimensionUnitList.Clear();
00145                 heightDimensionAttribute.DimensionUnitList.Add(new DimensionUnit
00146                 {
00147                     Value = measuredHtmlElementDimensions.HeightInPixels,
00148                     DimensionUnitKind = DimensionUnitKind.Pixels
00149                 });
00150             }
00151 
00152             // Left
00153             {
00154                 var leftDimensionAttribute = elementDimensions.DimensionAttributeList.First(
00155                     x => x.DimensionAttributeKind == DimensionAttributeKind.Left);
00156 
00157                 leftDimensionAttribute.DimensionUnitList.Clear();
00158                 leftDimensionAttribute.DimensionUnitList.Add(new DimensionUnit
00159                 {
00160                     Value = measuredHtmlElementDimensions.LeftInPixels,
00161                     DimensionUnitKind = DimensionUnitKind.Pixels
00162                 });
00163             }
00164 
00165             // Top
00166             {
00167                 var topDimensionAttribute = elementDimensions.DimensionAttributeList.First(
00168                     x => x.DimensionAttributeKind == DimensionAttributeKind.Top);
00169 
00170                 topDimensionAttribute.DimensionUnitList.Clear();
