00190     public Task OnDragEndAsync(MouseEventArgs mouseEventArgs, IDropzone? dropzone)
00191     {
00192         var panelGroup = TabGroup as PanelGroup;
00193 
00194         if (dropzone is not PanelGroupDropzone panelGroupDropzone)
00195             return Task.CompletedTask;
00196 
00197         // Create Dialog
00198         if (panelGroupDropzone.PanelGroupKey == Key<PanelGroup>.Empty)
00199         {
00200             // Delete current UI
00201             {
00202                 if (panelGroup is not null)
00203                 {
00204                     Dispatcher.Dispatch(new PanelState.DisposePanelTabAction(
00205                         panelGroup.Key,
00206                         Key));
00207                 }
00208                 else
00209                 {
00210                     // Is a dialog
00211                     //
00212                     // Already a dialog, so nothing needs to be done
00213                     return Task.CompletedTask;
00214                 }
00215 
00216                 TabGroup = null;
00217             }
00218 
00219             DialogService.RegisterDialogRecord(this);
00220         }
00221         
00222         // Create Panel Tab
00223         {
00224             // Delete current UI
00225             {
00226                 if (panelGroup is not null)
00227                 {
00228                     Dispatcher.Dispatch(new PanelState.DisposePanelTabAction(
00229                         panelGroup.Key,
00230                         Key));
00231                 }
00232                 else
00233                 {
00234                     DialogService.DisposeDialogRecord(DynamicViewModelKey);
00235                 }
00236 
00237                 TabGroup = null;
00238             }
00239 
00240             var verticalHalfwayPoint = dropzone.MeasuredHtmlElementDimensions.TopInPixels +
00241                 (dropzone.MeasuredHtmlElementDimensions.HeightInPixels / 2);
00242 
00243             var insertAtIndexZero = mouseEventArgs.ClientY < verticalHalfwayPoint
00244                 ? true
00245                 : false;
00246 
00247             Dispatcher.Dispatch(new PanelState.RegisterPanelTabAction(
00248                 panelGroupDropzone.PanelGroupKey,
00249                 this,
00250                 insertAtIndexZero));
00251         }
00252 
00253         return Task.CompletedTask;
00254     }
