00070     [Fact]
00071     public void ReduceRegisterPanelTabAction()
00072     {
00073         InitializePanelStateReducerTests(
00074             out var panelGroup,
00075             out var panelTab,
00076             out var panelStateWrap,
00077             out var dispatcher,
00078             out var serviceProvider);
00079 
00080         dispatcher.Dispatch(new PanelState.RegisterPanelGroupAction(panelGroup));
00081         Assert.Contains(panelStateWrap.Value.PanelGroupList, x => x == panelGroup);
00082 
00083         // Ensure the panelGroup is not empty.
00084         // This allows one to test the 'insertAtIndexZero' logic.
00085         // That is to say, insert at start or end logic.
00086         {
00087             dispatcher.Dispatch(new PanelState.RegisterPanelTabAction(
00088                 panelGroup.Key,
00089                 panelTab,
00090                 true));
00091 
00092             var localPanelGroup = panelStateWrap.Value.PanelGroupList.Single(x => x.Key == panelGroup.Key);
00093             Assert.Contains(localPanelGroup.TabList, x => x == panelTab);
00094             Assert.NotEmpty(localPanelGroup.TabList);
00095         }
00096 
00097         // InsertAtIndexZero == false
00098         {
00099             var insertAtIndexZero = false;
00100 
00101             var localPanelTab = new Panel(
00102                 "Solution Explorer",
00103                 Key<Panel>.NewKey(),
00104                 Key<IDynamicViewModel>.NewKey(),
00105                 ContextFacts.SolutionExplorerContext.ContextKey,
00106                 typeof(IconCSharpClassFragment),
00107                 new(),
00108                 dispatcher,
00109                 serviceProvider.GetRequiredService<IDialogService>(),
00110                 serviceProvider.GetRequiredService<IJSRuntime>());
00111 
00112             dispatcher.Dispatch(new PanelState.RegisterPanelTabAction(
00113                 panelGroup.Key,
00114                 localPanelTab,
00115                 insertAtIndexZero));
00116 
00117             var localPanelGroup = panelStateWrap.Value.PanelGroupList.Single(x => x.Key == panelGroup.Key);
00118             Assert.Contains(localPanelGroup.TabList, x => x == localPanelTab);
00119             Assert.Equal(localPanelGroup.TabList.Last(), localPanelTab);
00120         }
00121 
00122         // InsertAtIndexZero == true
00123         {
00124             var insertAtIndexZero = true;
00125 
00126             var localPanelTab = new Panel(
00127                 "Solution Explorer",
00128                 Key<Panel>.NewKey(),
00129                 Key<IDynamicViewModel>.NewKey(),
00130                 ContextFacts.SolutionExplorerContext.ContextKey,
00131                 typeof(IconCSharpClassFragment),
00132                 new(),
00133                 dispatcher,
00134                 serviceProvider.GetRequiredService<IDialogService>(),
00135                 serviceProvider.GetRequiredService<IJSRuntime>());
00136 
00137             dispatcher.Dispatch(new PanelState.RegisterPanelTabAction(
00138                 panelGroup.Key,
00139                 localPanelTab,
00140                 insertAtIndexZero));
00141 
00142             var localPanelGroup = panelStateWrap.Value.PanelGroupList.Single(x => x.Key == panelGroup.Key);
00143             Assert.Contains(localPanelGroup.TabList, x => x == localPanelTab);
00144             Assert.Equal(localPanelGroup.TabList.First(), localPanelTab);
00145         }
00146     }
