00201 
00202         dispatcher.Dispatch(new PanelState.RegisterPanelTabAction(
00203                 panelGroup.Key,
00204                 panelTab,
00205                 true));
00206 
00207         var localPanelGroup = panelStateWrap.Value.PanelGroupList.Single(x => x.Key == panelGroup.Key);
00208         Assert.Contains(localPanelGroup.TabList, x => x == panelTab);
