00224     [Fact]
00225     public void ReduceSetPanelTabAsActiveByContextRecordKeyAction()
00226     {
00227         InitializePanelStateReducerTests(
00228             out var panelGroup,
00229             out var panelTab,
00230             out var panelStateWrap,
00231             out var dispatcher,
00232             out var serviceProvider);
00233 
00234         Assert.Empty(panelGroup.TabList);
00235 
00236         dispatcher.Dispatch(new PanelState.RegisterPanelGroupAction(panelGroup));
00237         Assert.Contains(panelStateWrap.Value.PanelGroupList, x => x == panelGroup);
00238 
00239         List<(ContextRecord contextRecord, IPanelTab panelTab)> panelTabTupleList = new();
00240 
00241         foreach (var context in ContextFacts.AllContextsList)
00242         {
00243             var localPanelTab = new Panel(
00244                 "Solution Explorer",
00245                 Key<Panel>.NewKey(),
00246                 Key<IDynamicViewModel>.NewKey(),
00247                 context.ContextKey,
00248                 typeof(IconCSharpClassFragment),
00249                 new(),
00250                 dispatcher,
00251                 serviceProvider.GetRequiredService<IDialogService>(),
00252                 serviceProvider.GetRequiredService<IJSRuntime>());
00253 
00254             panelTabTupleList.Add((context, localPanelTab));
00255 
00256             dispatcher.Dispatch(new PanelState.RegisterPanelTabAction(
00257                 panelGroup.Key,
00258                 localPanelTab,
00259                 false));
00260 
00261             var localPanelGroup = panelStateWrap.Value.PanelGroupList.Single(x => x.Key == panelGroup.Key);
00262             Assert.Contains(localPanelGroup.TabList, x => x == localPanelTab);
00263         }
00264 
00265         panelTabTupleList.Reverse();
00266 
00267         foreach (var panelTabTuple in panelTabTupleList)
00268         {
00269             dispatcher.Dispatch(new PanelState.SetPanelTabAsActiveByContextRecordKeyAction(
00270                 panelTabTuple.contextRecord.ContextKey));
00271 
00272             var localPanelGroup = panelStateWrap.Value.PanelGroupList.Single(x => x.Key == panelGroup.Key);
00273             Assert.Equal(panelTabTuple.panelTab.Key, localPanelGroup.ActiveTabKey);
00274         }
00275     }
