00308     private void InitializePanelStateReducerTests(
00309         out PanelGroup samplePanelGroup,
00310         out IPanelTab samplePanelTab,
00311         out IState<PanelState> panelStateWrap,
00312         out IDispatcher dispatcher,
00313         out ServiceProvider serviceProvider)
00314     {
00315         var backgroundTaskService = new BackgroundTaskServiceSynchronous();
00316 
00317         var serviceCollection = new ServiceCollection()
00318             .AddScoped<IJSRuntime, DoNothingJsRuntime>()
00319             .AddLuthetusCommonServices(
00320                 new LuthetusHostingInformation(
00321                     LuthetusHostingKind.UnitTestingSynchronous,
00322                     LuthetusPurposeKind.Common,
00323                     backgroundTaskService))
00324             .AddFluxor(options => options.ScanAssemblies(typeof(LuthetusCommonConfig).Assembly));
00325 
00326         serviceProvider = serviceCollection.BuildServiceProvider();
00327         
00328         var store = serviceProvider.GetRequiredService<IStore>();
00329         store.InitializeAsync().Wait();
00330 
00331         panelStateWrap = serviceProvider.GetRequiredService<IState<PanelState>>();
00332         dispatcher = serviceProvider.GetRequiredService<IDispatcher>();
00333 
00334         samplePanelGroup = new PanelGroup(
00335             Key<PanelGroup>.NewKey(),
00336             Key<Panel>.Empty,
00337             new ElementDimensions(),
00338             ImmutableArray<IPanelTab>.Empty);
00339 
00340         samplePanelGroup.Dispatcher = dispatcher;
00341 
00342         var leftPanelGroupWidth = samplePanelGroup.ElementDimensions.DimensionAttributeList
00343             .Single(da => da.DimensionAttributeKind == DimensionAttributeKind.Width);
00344 
00345         leftPanelGroupWidth.DimensionUnitList.AddRange(new[]
00346         {
00347             new DimensionUnit
00348             {
00349                 Value = 33.3333,
00350                 DimensionUnitKind = DimensionUnitKind.Percentage
00351             },
00352             new DimensionUnit
00353             {
00354                 Value = ResizableColumn.RESIZE_HANDLE_WIDTH_IN_PIXELS / 2,
00355                 DimensionUnitKind = DimensionUnitKind.Pixels,
00356                 DimensionOperatorKind = DimensionOperatorKind.Subtract
00357             }
00358         });
00359 
00360         samplePanelTab = new Panel(
00361             "Solution Explorer",
00362             Key<Panel>.NewKey(),
00363             Key<IDynamicViewModel>.NewKey(),
00364             ContextFacts.SolutionExplorerContext.ContextKey,
00365             typeof(IconCSharpClassFragment),
00366             null,
00367             dispatcher,
00368             serviceProvider.GetRequiredService<IDialogService>(),
