00300     private Task ParseSolution(Key<DotNetSolutionModel> dotNetSolutionModelKey)
00301     {
00302         var dotNetSolutionState = _dotNetSolutionStateWrap.Value;
00303 
00304         var dotNetSolutionModel = dotNetSolutionState.DotNetSolutionsList.FirstOrDefault(
00305             x => x.Key == dotNetSolutionModelKey);
00306 
00307         if (dotNetSolutionModel is null)
00308             return Task.CompletedTask;
00309             
00310         _ = Task.Run(async () =>
00311         {
00312             var progressBarModel = new ProgressBarModel(0, ""parsing..."");
00313 
00314             NotificationHelper.DispatchProgress(
00315                 $""Parse: {dotNetSolutionModel.AbsolutePath.NameWithExtension}"",
00316                 progressBarModel,
00317                 _commonComponentRenderers,
00318                 _dispatcher,
00319                 TimeSpan.FromMilliseconds(-1));
00320                 
00321             var progressThrottle = new Throttle(TimeSpan.FromMilliseconds(100));
00322         
00323             try
00324             {
00325                 if (_textEditorService.TextEditorConfig.RegisterModelFunc is null)
00326                     return;
00327                     
00328                 progressThrottle.Run(_ => 
00329                 {
00330                     progressBarModel.SetProgress(0.05, ""Discovering projects..."");
00331                     return Task.CompletedTask;
00332                 });
00333                 
00334                 foreach (var project in dotNetSolutionModel.DotNetProjectList)
00335                 {
00336                     RegisterStartupControl(project);
00337                 
00338                     var resourceUri = new ResourceUri(project.AbsolutePath.Value);
00339 
00340                     if (!await _fileSystemProvider.File.ExistsAsync(resourceUri.Value))
00341                         continue; // TODO: This can still cause a race condition exception if the file is removed before the next line runs.
00342 
00343                     var registerModelArgs = new RegisterModelArgs(resourceUri, _serviceProvider)
00344                     {
00345                         ShouldBlockUntilBackgroundTaskIsCompleted = true,
00346                     };
00347 
00348                     await _textEditorService.TextEditorConfig.RegisterModelFunc.Invoke(registerModelArgs)
00349                         .ConfigureAwait(false);
00350                 }
00351 
00352                 var previousStageProgress = 0.05;
00353                 var dotNetProjectListLength = dotNetSolutionModel.DotNetProjectList.Length;
00354                 var projectsParsedCount = 0;
00355                 foreach (var project in dotNetSolutionModel.DotNetProjectList)
00356                 {
00357                     // foreach project in solution
00358                     //  foreach C# file in project
00359                     //      EnqueueBackgroundTask(async () =>
00360                     //      {
00361                     //          ParseCSharpFile();
00362                     //          UpdateProgressBar();
00363                     //      });
00364                     //
00365                     // Treat every project as an equal weighting with relation to remaining percent to complete
00366                     // on the progress bar.
00367                     //
00368                     // If the project were to be parsed, how much would it move the percent progress completed by?
00369                     //
00370                     // Then, in order to see progress while each C# file in the project gets parsed,
00371                     // multiply the percent progress this project can provide by the proportion
00372                     // of the project's C# files which have been parsed.
00373                     var maximumProgressAvailableToProject = (1 - previousStageProgress) * ((double)1.0 / dotNetProjectListLength);
00374                     var currentProgress = Math.Min(1.0, previousStageProgress + maximumProgressAvailableToProject * projectsParsedCount);
00375 
00376                     // This 'SetProgress' is being kept out the throttle, since it sets message 1
00377                     // whereas the per class progress updates set message 2.
00378                     //
00379                     // Otherwise an update to message 2 could result in this message 1 update never being written.
00380                     progressBarModel.SetProgress(
00381                         currentProgress,
00382                         $""{projectsParsedCount + 1}/{dotNetProjectListLength}: {project.AbsolutePath.NameWithExtension}"");
00383                     
00384 
00385                     await DiscoverClassesInProject(project, progressBarModel, progressThrottle, currentProgress, maximumProgressAvailableToProject);
00386                     projectsParsedCount++;
00387                 }
00388 
00389                 progressThrottle.Run(_ => 
00390                 {
00391                     progressBarModel.SetProgress(1, $""Finished parsing: {dotNetSolutionModel.AbsolutePath.NameWithExtension}"", string.Empty);
00392                     progressBarModel.Dispose();
00393                     return Task.CompletedTask;
00394                 });
00395             }
00396             catch (Exception e)
00397             {
00398                 var currentProgress = progressBarModel.GetProgress();
00399                 
00400                 progressThrottle.Run(_ => 
00401                 {
00402                     progressBarModel.SetProgress(currentProgress, e.ToString());
00403                     progressBarModel.Dispose();
00404                     return Task.CompletedTask;
00405                 });
00406             }
00407         });
00408 
00409         return Task.CompletedTask;
00410     }
