00412     private async Task DiscoverClassesInProject(
00413         IDotNetProject dotNetProject,
00414         ProgressBarModel progressBarModel,
00415         Throttle progressThrottle,
00416         double currentProgress,
00417         double maximumProgressAvailableToProject)
00418     {
00419         if (!await _fileSystemProvider.File.ExistsAsync(dotNetProject.AbsolutePath.Value))
00420             return; // TODO: This can still cause a race condition exception if the file is removed before the next line runs.
00421 
00422         var parentDirectory = dotNetProject.AbsolutePath.ParentDirectory;
00423         if (parentDirectory is null)
00424             return;
00425 
00426         var startingAbsolutePathForSearch = parentDirectory.Value;
00427         var discoveredFileList = new List<string>();
00428 
00429         progressThrottle.Run(_ => 
00430         {
00431             progressBarModel.SetProgress(null, null, ""discovering files"");
00432             return Task.CompletedTask;
00433         });
00434         
00435         await DiscoverFilesRecursively(startingAbsolutePathForSearch, discoveredFileList, true).ConfigureAwait(false);
00436 
00437         await ParseClassesInProject(
00438             dotNetProject,
00439             progressBarModel,
00440             progressThrottle,
00441             currentProgress,
00442             maximumProgressAvailableToProject,
00443             discoveredFileList);
