00445         async Task DiscoverFilesRecursively(string directoryPathParent, List<string> discoveredFileList, bool isFirstInvocation)
00446         {
00447             var directoryPathChildList = await _fileSystemProvider.Directory.GetDirectoriesAsync(
00448                     directoryPathParent,
00449                     CancellationToken.None)
00450                 .ConfigureAwait(false);
00451 
00452             var filePathChildList = await _fileSystemProvider.Directory.GetFilesAsync(
00453                     directoryPathParent,
00454                     CancellationToken.None)
00455                 .ConfigureAwait(false);
00456 
00457             foreach (var filePathChild in filePathChildList)
00458             {
00459                 if (filePathChild.EndsWith("".cs""))
00460                     discoveredFileList.Add(filePathChild);
00461             }
00462 
00463             //var progressMessage = progressBarModel.Message ?? string.Empty;
00464 
00465             foreach (var directoryPathChild in directoryPathChildList)
00466             {
00467                 if (directoryPathChild.Contains("".git"") ||
00468                     directoryPathChild.Contains("".vs"") ||
00469                     directoryPathChild.Contains("".vscode"") ||
00470                     directoryPathChild.Contains("".idea"") ||
00471                     directoryPathChild.Contains(""bin"") ||
00472                     directoryPathChild.Contains(""obj""))
00473                 {
00474                     continue;
00475                 }
00476 
00477                 //if (isFirstInvocation)
00478                 //{
00479                 //  var currentProgress = progressBarModel.GetProgress();
00480                 // progressThrottle.Run(_ => 
00481                 // {
00482                 //  progressBarModel.SetProgress(currentProgress, $""{directoryPathChild} "" + progressMessage);
00483                 //  return Task.CompletedTask;
00484                 // });
00485                 //  
00486                 //}
00487 
00488                 await DiscoverFilesRecursively(directoryPathChild, discoveredFileList, isFirstInvocation: false).ConfigureAwait(false);
00489             }
00490         }
