00493     private async Task ParseClassesInProject(
00494         IDotNetProject dotNetProject,
00495         ProgressBarModel progressBarModel,
00496         Throttle progressThrottle,
00497         double currentProgress,
00498         double maximumProgressAvailableToProject,
00499         List<string> discoveredFileList)
00500     {
00501         var fileParsedCount = 0;
00502         
00503         foreach (var file in discoveredFileList)
00504         {
00505             var fileAbsolutePath = _environmentProvider.AbsolutePathFactory(file, false);
00506 
00507             var progress = currentProgress + maximumProgressAvailableToProject * (fileParsedCount / (double)discoveredFileList.Count);
00508 
00509             progressThrottle.Run(_ => 
00510             {
00511                 progressBarModel.SetProgress(
00512                     progress,
00513                     null,
00514                     $""{fileParsedCount + 1}/{discoveredFileList.Count}: {fileAbsolutePath.NameWithExtension}"");
00515                 return Task.CompletedTask;
00516             });
00517 
00518             var resourceUri = new ResourceUri(file);
00519             
00520             var registerModelArgs = new RegisterModelArgs(resourceUri, _serviceProvider)
00521             {
00522                 ShouldBlockUntilBackgroundTaskIsCompleted = true,
00523             };
00524 
00525             await _textEditorService.TextEditorConfig.RegisterModelFunc.Invoke(registerModelArgs)
00526                 .ConfigureAwait(false);
00527                 
00528             fileParsedCount++;
00529         }
00530     }
