00541     private async Task SetDotNetSolutionTreeViewAsync(Key<DotNetSolutionModel> dotNetSolutionModelKey)
00542     {
00543         var dotNetSolutionState = _dotNetSolutionStateWrap.Value;
00544 
00545         var dotNetSolutionModel = dotNetSolutionState.DotNetSolutionsList.FirstOrDefault(
00546             x => x.Key == dotNetSolutionModelKey);
00547 
00548         if (dotNetSolutionModel is null)
00549             return;
00550 
00551         var rootNode = new TreeViewSolution(
00552             dotNetSolutionModel,
00553             _dotNetComponentRenderers,
00554             _ideComponentRenderers,
00555             _commonComponentRenderers,
00556             _fileSystemProvider,
00557             _environmentProvider,
00558             true,
00559             true);
00560 
00561         await rootNode.LoadChildListAsync().ConfigureAwait(false);
00562 
00563         if (!_treeViewService.TryGetTreeViewContainer(DotNetSolutionState.TreeViewSolutionExplorerStateKey, out _))
00564         {
00565             _treeViewService.RegisterTreeViewContainer(new TreeViewContainer(
00566                 DotNetSolutionState.TreeViewSolutionExplorerStateKey,
00567                 rootNode,
00568                 new TreeViewNoType[] { rootNode }.ToImmutableList()));
00569         }
00570         else
00571         {
00572             _treeViewService.SetRoot(DotNetSolutionState.TreeViewSolutionExplorerStateKey, rootNode);
00573 
00574             _treeViewService.SetActiveNode(
00575                 DotNetSolutionState.TreeViewSolutionExplorerStateKey,
00576                 rootNode,
00577                 true,
00578                 false);
00579         }
00580 
00581         if (dotNetSolutionModel is null)
00582             return;
00583 
00584         _dispatcher.Dispatch(ConstructModelReplacement(
00585             dotNetSolutionModel.Key,
00586             dotNetSolutionModel));
00587     }
