00603     private Task StartButtonOnClick(IStartupControlModel interfaceStartupControlModel, IDotNetProject project)
00604     {
00605         var startupControlModel = (StartupControlModel)interfaceStartupControlModel;
00606         
00607         var ancestorDirectory = project.AbsolutePath.ParentDirectory;
00608 
00609         if (ancestorDirectory is null)
00610             return Task.CompletedTask;
00611 
00612         var formattedCommand = DotNetCliCommandFormatter.FormatStartProjectWithoutDebugging(
00613             project.AbsolutePath);
00614             
00615         var terminalCommandRequest = new TerminalCommandRequest(
00616             formattedCommand.Value,
00617             ancestorDirectory.Value,
00618             _newDotNetSolutionTerminalCommandRequestKey)
00619         {
00620             BeginWithFunc = parsedCommand =>
00621             {
00622                 _dotNetCliOutputParser.ParseOutputEntireDotNetRun(
00623                     string.Empty);
00624                     
00625                 return Task.CompletedTask;
00626             },
00627             ContinueWithFunc = parsedCommand =>
00628             {
00629                 startupControlModel.ExecutingTerminalCommandRequest = null;
00630                 _dispatcher.Dispatch(new StartupControlState.StateChangedAction());
00631             
00632                 _dotNetCliOutputParser.ParseOutputEntireDotNetRun(
00633                     parsedCommand.OutputCache.ToString());
00634                     
00635                 return Task.CompletedTask;
00636             }
00637         };
00638         
