00673     private async Task Website_AddExistingProjectToSolutionAsync(
00674         Key<DotNetSolutionModel> dotNetSolutionModelKey,
00675         string projectTemplateShortName,
00676         string cSharpProjectName,
00677         IAbsolutePath cSharpProjectAbsolutePath)
00678     {
00679         var inDotNetSolutionModel = _dotNetSolutionStateWrap.Value.DotNetSolutionsList.FirstOrDefault(
00680             x => x.Key == dotNetSolutionModelKey);
00681 
00682         if (inDotNetSolutionModel is null)
00683             return;
00684 
00685         var projectTypeGuid = WebsiteProjectTemplateFacts.GetProjectTypeGuid(
00686             projectTemplateShortName);
00687 
00688         var relativePathFromSlnToProject = PathHelper.GetRelativeFromTwoAbsolutes(
00689             inDotNetSolutionModel.NamespacePath.AbsolutePath,
00690             cSharpProjectAbsolutePath,
00691             _environmentProvider);
00692 
00693         var projectIdGuid = Guid.NewGuid();
00694 
00695         var dotNetSolutionModelBuilder = new DotNetSolutionModelBuilder(inDotNetSolutionModel);
00696 
00697         var cSharpProject = new CSharpProjectModel(
00698             cSharpProjectName,
00699             projectTypeGuid,
00700             relativePathFromSlnToProject,
00701             projectIdGuid,
00702             // TODO: 'openAssociatedGroupToken' gets set when 'AddDotNetProject(...)' is ran, which is hacky and should be changed. Until then passing in 'null!'
00703             null!,
00704             null,
00705             cSharpProjectAbsolutePath);
00706 
00707         dotNetSolutionModelBuilder.AddDotNetProject(cSharpProject, _environmentProvider);
00708 
00709         var outDotNetSolutionModel = dotNetSolutionModelBuilder.Build();
00710 
00711         await _fileSystemProvider.File.WriteAllTextAsync(
00712                 outDotNetSolutionModel.NamespacePath.AbsolutePath.Value,
00713                 outDotNetSolutionModel.SolutionFileContents)
00714             .ConfigureAwait(false);
00715 
00716         var solutionTextEditorModel = _textEditorService.ModelApi.GetOrDefault(
00717             new ResourceUri(inDotNetSolutionModel.NamespacePath.AbsolutePath.Value));
00718 
00719         if (solutionTextEditorModel is not null)
00720         {
00721             _textEditorService.PostUnique(
00722                 nameof(Website_AddExistingProjectToSolutionAsync),
00723                 editContext =>
00724                 {
00725                     var modelModifier = editContext.GetModelModifier(solutionTextEditorModel.ResourceUri);
00726                     if (modelModifier is null)
00727                         return Task.CompletedTask;
00728                 
00729                     _textEditorService.ModelApi.Reload(
00730                         editContext,
00731                         modelModifier,
00732                         outDotNetSolutionModel.SolutionFileContents,
00733                         DateTime.UtcNow);
00734                     return Task.CompletedTask;
00735                 });
00736         }
00737 
00738         // TODO: Putting a hack for now to overwrite if somehow model was registered already
00739         _dispatcher.Dispatch(ConstructModelReplacement(
00740             outDotNetSolutionModel.Key,
00741             outDotNetSolutionModel));
00742 
00743         await SetDotNetSolutionTreeViewAsync(outDotNetSolutionModel.Key).ConfigureAwait(false);
00744     }
