00752     public record WithAction(Func<DotNetSolutionState, DotNetSolutionState> WithFunc)
00753         : IWithAction;
00754 
00755     public static IWithAction ConstructModelReplacement(
00756             Key<DotNetSolutionModel> dotNetSolutionModelKey,
00757             DotNetSolutionModel outDotNetSolutionModel)
00758     {
00759         return new WithAction(dotNetSolutionState =>
00760         {
00761             var indexOfSln = dotNetSolutionState.DotNetSolutionsList.FindIndex(
00762                 sln => sln.Key == dotNetSolutionModelKey);
00763 
00764             if (indexOfSln == -1)
00765                 return dotNetSolutionState;
00766 
00767             var outDotNetSolutions = dotNetSolutionState.DotNetSolutionsList.SetItem(
00768                 indexOfSln,
00769                 outDotNetSolutionModel);
00770 
00771             return dotNetSolutionState with
00772             {
00773                 DotNetSolutionsList = outDotNetSolutions
00774             };
00775         });
00776     }
