00056             nameof(PartitionTests),
00057             editContext =>
00058             {
00059                 try
00060                 {
00061                     var modelModifier = editContext.GetModelModifier(model.ResourceUri);
00062 
00063                     if (modelModifier is null)
00064                     {
00065                         Console.WriteLine("modelModifier is null");
00066                         return Task.CompletedTask;
00067                     }
00068                     
00069                     // The cursor uses line and column indices
00070                     //
00071                     // But the selection uses position indices.
00072                     //
00073                     // These position indices are dangerous, because one might accidentally
00074                     // use an index that is among a multi-byte character.
00075                     //
00076                     // When using the MoveCursor and etc, 
00077                     
00078                     LogAndNonScientificallyAssertPartitionList(modelModifier, "[\\r\\n][\\r\\n\\r\\n]");
00079                     
00080                     // "\r\n\r\n\r\n";
00081                     // "[\r\n][\r\n\r\n]"
00082                     //   ^^^^^^^^^^
00083                     //
00084                     // Delete the letter above the '^'.
00085                     //
00086                     // So, delete the only line ending in partition 1
00087                     // and the first line ending in partition 2.
00088                     //
00089                     // Do this by selecting the text then insert an enter key.
00090                     
00091                     var cursor = new TextEditorCursor(isPrimaryCursor: true);
00092                     var cursorModifier = new TextEditorCursorModifier(cursor);
00093                     
00094                     cursorModifier.SelectionAnchorPositionIndex = 0;
00095                     cursorModifier.SelectionEndingPositionIndex = 4;
00096                     
00097                     cursorModifier.LineIndex = 2;
00098                     cursorModifier.ColumnIndex = 0;
00099                     
00100                     var cursorModifierBag = new CursorModifierBagTextEditor(
00101                         Key<TextEditorViewModel>.Empty,
00102                         new List<TextEditorCursorModifier> { cursorModifier });
00103                         
00104                     modelModifier.Delete(
00105                         cursorModifierBag,
00106                         columnCount: 0, // Delete the selection, odd to give 0?
00107                         expandWord: false,
00108                         TextEditorModelModifier.DeleteKind.Delete);
00109                     
00110                     LogAndNonScientificallyAssertPartitionList(modelModifier, "[][\\r\\n]");
00111                     
00112                     return Task.CompletedTask;
00113                 }
00114                 catch (Exception e)
00115                 {
00116                     exception = e;
00117                     return Task.CompletedTask;
00118                 }
00119             });
00120 
00121         await test.TextEditorService.PostAsync(uniqueTextEditorWork).ConfigureAwait(false);
00122 
00123         if (exception is not null)
00124             throw exception;
00125     }
