00321         // Input
00322         // =====
00323         // var sourceText = "\r\n\r\n\r\n\r\n";
00324         // partitionSize: 4
00325 
00326         // Build Partition
00327         // ===============
00328         // []
00329         // [\r]
00330         // [\r\n]
00331         // [\r\n\r]
00332         // [\r\n\r\n]
00333         // Attempt to insert '\r'
00334         // Cause a split
00335         // [\r\n][\r\n]
00336         // Again, attempt to insert '\r'
00337         // [\r\n][\r\n\r]
00338         // [\r\n][\r\n\r\n]
00339         // Attempt to insert '\r'
00340         // Cause a split
00341         // [\r\n][\r\n][\r\n]
00342         // Again, attempt to insert '\r'
00343         // [\r\n][\r\n][\r\n\r\n]
00344 
00345         // Delete Text via text selection, then delete key
00346         // ===============================================
00347         // "[\r\n][\r\n][\r\n\r\n]"
00348         //   ^^^^^^^^^^^^^^^^
00349 
00350         // Method invocation should look like:
00351         // ===================================
00352         // __RemoveRange(0, 6);
