00465     private void LogAndNonScientificallyAssertPartitionList(
00466         ITextEditorModel model,
00467         string expectedVisualizationText)
00468     {
00469         /*
00470 [abcd][efgh][ijkl][mnop][qrst][uvwx][yz]
00471 
00472 [abcd]
00473     insert e
00474 [ab][cdef]
00475     insert g
00476 [abc][cd][efg]
00477 
00478 -------------------
00479 
00480 PartitionSize = 4
00481 "\r\n\r\n\r\n"
00482 
00483 [\r\n\r\n]
00484     insert \r
00485 [\r\n][\r\n\r\n]
00486 
00487 */  
00488         var actualVisualizationTextBuilder = new StringBuilder();
00489         
00490         Console.WriteLine("\n\nLogPartition_START\n");
00491         
00492         foreach (var partition in model.PartitionList)
00493         {
00494             actualVisualizationTextBuilder.Append('[');
00495             foreach (var richCharacter in partition.RichCharacterList)
00496             {
00497                 if (richCharacter.Value == '\r')
00498                 {
00499                     actualVisualizationTextBuilder.Append("\\r");
00500                 }
00501                 else if (richCharacter.Value == '\n')
00502                 {
00503                     actualVisualizationTextBuilder.Append("\\n");
00504                 }
00505                 else if (richCharacter.Value == '\t')
00506                 {
00507                     actualVisualizationTextBuilder.Append("\\t");
00508                 }
00509                 else
00510                 {
00511                     actualVisualizationTextBuilder.Append(richCharacter.Value);
00512                 }
00513             }
00514             actualVisualizationTextBuilder.Append(']');
00515         }
00516         
00517         var actualVisualizationText = actualVisualizationTextBuilder.ToString();
00518         Console.Write(actualVisualizationText);
00519         
00520         Console.WriteLine("\n\nLogPartition_END\n\n");
00521         
00522         Assert.Equal(expectedVisualizationText, actualVisualizationText);
00523     }
