01157     public class TestContext
01158     {
01159         public TestContext(LuthetusHostingInformation hostingInformation, IServiceProvider serviceProvider)
01160         {
01161             HostingInformation = hostingInformation;
01162             ServiceProvider = serviceProvider;
01163             
01164             TextEditorService = ServiceProvider.GetRequiredService<ITextEditorService>();
01165             ContinuousBackgroundTaskWorker = ServiceProvider.GetRequiredService<ContinuousBackgroundTaskWorker>();
01166             
01167             HostingInformation.StartBackgroundTaskWorkers(serviceProvider);
01168         }
01169     
01170         public LuthetusHostingInformation HostingInformation { get; }
01171         public IServiceProvider ServiceProvider { get; }
01172         
01173         public ITextEditorService TextEditorService { get; }
01174         public ContinuousBackgroundTaskWorker ContinuousBackgroundTaskWorker { get; }
01175     }
01176     
01177     public TestContext TestInitialize()
01178     {
01179         var services = new ServiceCollection();
01180         
01181         var hostingInformation = new LuthetusHostingInformation(
01182             LuthetusHostingKind.UnitTestingAsync,
01183             LuthetusPurposeKind.TextEditor,
01184             new BackgroundTaskService());
01185         
01186         services
01187             .AddLuthetusTextEditor(hostingInformation)
01188             .AddScoped<ILoggerFactory, NullLoggerFactory>()
01189             .AddScoped<IJSRuntime, DoNothingJsRuntime>();
01190             
01191         // If one doesn't use a LuthetusPurposeKind of Photino,
01192         // then this code block is redundant, because the ServieCollectionExtensions.cs
01193         // for Luthetus.Common will register InMemory for Environment and FileSystem providers.
01194         //
01195         // But, because of the importance that under no circumstance one's actual FileSystem is used during
01196         // unit testing, this redundancy is here.
01197         {
01198             services.AddScoped<IEnvironmentProvider, InMemoryEnvironmentProvider>();
01199             services.AddScoped<IFileSystemProvider, InMemoryFileSystemProvider>();
01200         }
01201         
01202         // At the moment one has to manually set the following services to their InMemory implementation.
01203         {
01204             services.AddScoped<IClipboardService, InMemoryClipboardService>();
01205             services.AddScoped<IStorageService, InMemoryStorageService>();
01206         }
01207         
01208         services.AddFluxor(options => options.ScanAssemblies(
01209             typeof(LuthetusCommonConfig).Assembly,
01210             typeof(LuthetusTextEditorConfig).Assembly));
01211         
01212         var testContext = new TestContext(hostingInformation, services.BuildServiceProvider());
01213         
01214         // Check various services that should be InMemory implementations.
01215         {   
01216             {
01217                 var fileSystemProvider = testContext.ServiceProvider.GetRequiredService<IFileSystemProvider>();
01218                 if (fileSystemProvider is not InMemoryFileSystemProvider)
01219                     throw new LuthetusUnitTestException(
01220                         $"A UnitTest's {nameof(IFileSystemProvider)} must be " +
01221                         $"{nameof(InMemoryFileSystemProvider)}, " +
01222                         $"but was {fileSystemProvider.GetType().Name}");
01223             }
01224             
01225             {
01226                 var environmentProvider = testContext.ServiceProvider.GetRequiredService<IEnvironmentProvider>();
01227                 if (environmentProvider is not InMemoryEnvironmentProvider)
01228                     throw new LuthetusUnitTestException(
01229                         $"A UnitTest's {nameof(IEnvironmentProvider)} must be " +
01230                         $"{nameof(InMemoryEnvironmentProvider)}, " +
01231                         $"but was {environmentProvider.GetType().Name}");
01232             }
01233     
01234             {
01235                 var clipboardService = testContext.ServiceProvider.GetRequiredService<IClipboardService>();
01236                 if (clipboardService is not InMemoryClipboardService)
01237                     throw new LuthetusUnitTestException(
01238                         $"A UnitTest's {nameof(IClipboardService)} must be " +
01239                         $"{nameof(InMemoryClipboardService)}, " +
01240                         $"but was {clipboardService.GetType().Name}");
01241             }
01242     
01243             {
01244                 var storageService = testContext.ServiceProvider.GetRequiredService<IStorageService>();
01245                 if (storageService is not InMemoryStorageService)
01246                     throw new LuthetusUnitTestException(
01247                         $"A UnitTest's {nameof(IStorageService)} must be " +
01248                         $"{nameof(InMemoryStorageService)}, " +
01249                         $"but was {storageService.GetType().Name}");
01250             }
01251         }
01252         
01253         var store = testContext.ServiceProvider.GetRequiredService<IStore>();
01254         store.InitializeAsync().Wait();
01255     
01256         return testContext;
01257     }
01258 }
