00033     public List<TextEditorPresentationModel> GetTextEditorPresentationModels(
00034         ImmutableList<Key<TextEditorPresentationModel>> textEditorPresentationKeys)
00035     {
00036         var renderBatchLocal = _renderBatch;
00037         if (renderBatchLocal is null)
00038             return new();
00039         
00040         var textEditorPresentationModelList = new List<TextEditorPresentationModel>();
00041 
00042         foreach (var presentationKey in textEditorPresentationKeys)
00043         {
00044             var textEditorPresentationModel = renderBatchLocal.Model.PresentationModelList.FirstOrDefault(x =>
00045                 x.TextEditorPresentationKey == presentationKey);
00046 
00047             if (textEditorPresentationModel is not null)
00048                 textEditorPresentationModelList.Add(textEditorPresentationModel);
00049         }
00050 
00051         return textEditorPresentationModelList;
00052     }
