00054     public string PresentationGetCssStyleString(
00055         int lowerPositionIndexInclusive,
00056         int upperPositionIndexExclusive,
00057         int rowIndex)
00058     {
00059         var renderBatchLocal = _renderBatch;
00060         if (renderBatchLocal is null)
00061             return string.Empty;
00062         
00063         try
00064         {
00065             var charMeasurements = renderBatchLocal.ViewModel.CharAndLineMeasurements;
00066             var textEditorDimensions = renderBatchLocal.ViewModel.TextEditorDimensions;
00067             var scrollbarDimensions = renderBatchLocal.ViewModel.ScrollbarDimensions;
00068 
00069             if (rowIndex >= renderBatchLocal.Model.LineEndList.Count)
00070                 return string.Empty;
00071 
00072             var line = renderBatchLocal.Model.GetLineInformation(rowIndex);
00073 
00074             var startingColumnIndex = 0;
00075             var endingColumnIndex = line.EndPositionIndexExclusive - 1;
00076 
00077             var fullWidthOfRowIsSelected = true;
00078 
00079             if (lowerPositionIndexInclusive > line.StartPositionIndexInclusive)
00080             {
00081                 startingColumnIndex = lowerPositionIndexInclusive - line.StartPositionIndexInclusive;
00082                 fullWidthOfRowIsSelected = false;
00083             }
00084 
00085             if (upperPositionIndexExclusive < line.EndPositionIndexExclusive)
00086             {
00087                 endingColumnIndex = upperPositionIndexExclusive - line.StartPositionIndexInclusive;
00088                 fullWidthOfRowIsSelected = false;
00089             }
00090 
00091             var topInPixelsInvariantCulture = (rowIndex * charMeasurements.LineHeight).ToCssValue();
00092 
00093             var top = $"top: {topInPixelsInvariantCulture}px;";
00094 
00095             var heightInPixelsInvariantCulture = charMeasurements.LineHeight.ToCssValue();
00096 
00097             var height = $"height: {heightInPixelsInvariantCulture}px;";
00098 
00099             var startInPixels = startingColumnIndex * charMeasurements.CharacterWidth;
00100 
00101             // startInPixels offset from Tab keys a width of many characters
00102             {
00103                 var tabsOnSameRowBeforeCursor = renderBatchLocal.Model.GetTabCountOnSameLineBeforeCursor(
00104                     rowIndex,
00105                     startingColumnIndex);
00106 
00107                 // 1 of the character width is already accounted for
00108                 var extraWidthPerTabKey = TextEditorModel.TAB_WIDTH - 1;
00109 
00110                 startInPixels += extraWidthPerTabKey * tabsOnSameRowBeforeCursor * charMeasurements.CharacterWidth;
00111             }
00112 
00113             var startInPixelsInvariantCulture = startInPixels.ToCssValue();
00114             var left = $"left: {startInPixelsInvariantCulture}px;";
00115 
00116             var widthInPixels = endingColumnIndex * charMeasurements.CharacterWidth - startInPixels;
00117 
00118             // Tab keys a width of many characters
00119             {
00120                 var tabsOnSameRowBeforeCursor = renderBatchLocal.Model.GetTabCountOnSameLineBeforeCursor(
00121                     rowIndex,
00122                     line.LastValidColumnIndex);
00123 
00124                 // 1 of the character width is already accounted for
00125                 var extraWidthPerTabKey = TextEditorModel.TAB_WIDTH - 1;
00126 
00127                 widthInPixels += extraWidthPerTabKey * tabsOnSameRowBeforeCursor * charMeasurements.CharacterWidth;
00128             }
00129 
00130             var widthCssStyleString = "width: ";
00131 
00132             var fullWidthValue = scrollbarDimensions.ScrollWidth;
00133 
00134             if (textEditorDimensions.Width > scrollbarDimensions.ScrollWidth)
00135                 fullWidthValue = textEditorDimensions.Width; // If content does not fill the viewable width of the Text Editor User Interface
00136 
00137             var fullWidthValueInPixelsInvariantCulture = fullWidthValue.ToCssValue();
00138 
00139             var widthInPixelsInvariantCulture = widthInPixels.ToCssValue();
00140 
00141             if (fullWidthOfRowIsSelected)
00142                 widthCssStyleString += $"{fullWidthValueInPixelsInvariantCulture}px;";
00143             else if (startingColumnIndex != 0 && upperPositionIndexExclusive > line.EndPositionIndexExclusive - 1)
00144                 widthCssStyleString += $"calc({fullWidthValueInPixelsInvariantCulture}px - {startInPixelsInvariantCulture}px);";
00145             else
00146                 widthCssStyleString += $"{widthInPixelsInvariantCulture}px;";
00147 
00148             return $"position: absolute; {top} {height} {left} {widthCssStyleString}";
00149         }
00150         catch (LuthetusTextEditorException)
00151         {
00152             return string.Empty;
00153         }
00154     }
