00161     public ImmutableArray<TextEditorTextSpan> PresentationVirtualizeAndShiftTextSpans(
00162         TextEditorTextModification[] textModifications,
00163         ImmutableArray<TextEditorTextSpan> inTextSpanList)
00164     {
00165         var renderBatchLocal = _renderBatch;
00166         if (renderBatchLocal is null)
00167             return ImmutableArray<TextEditorTextSpan>.Empty;
00168         
00169         try
00170         {
00171             // Virtualize the text spans
00172             var virtualizedTextSpanList = new List<TextEditorTextSpan>();
00173             if (renderBatchLocal.ViewModel.VirtualizationResult?.EntryList.Any() ?? false)
00174             {
00175                 var lowerLineIndexInclusive = renderBatchLocal.ViewModel.VirtualizationResult.EntryList.First().Index;
00176                 var upperLineIndexInclusive = renderBatchLocal.ViewModel.VirtualizationResult.EntryList.Last().Index;
00177 
00178                 var lowerLine = renderBatchLocal.Model.GetLineInformation(lowerLineIndexInclusive);
00179                 var upperLine = renderBatchLocal.Model.GetLineInformation(upperLineIndexInclusive);
00180 
00181                 foreach (var textSpan in inTextSpanList)
00182                 {
00183                     if (lowerLine.StartPositionIndexInclusive <= textSpan.StartingIndexInclusive &&
00184                         upperLine.EndPositionIndexExclusive >= textSpan.StartingIndexInclusive)
00185                     {
00186                         virtualizedTextSpanList.Add(textSpan);
00187                     }
00188                 }
00189             }
00190             else
00191             {
00192                 // No 'VirtualizationResult', so don't render any text spans.
00193                 return ImmutableArray<TextEditorTextSpan>.Empty;
00194             }
00195 
00196             var outTextSpansList = new List<TextEditorTextSpan>();
00197             // Shift the text spans
00198             {
00199                 foreach (var textSpan in virtualizedTextSpanList)
00200                 {
00201                     var startingIndexInclusive = textSpan.StartingIndexInclusive;
00202                     var endingIndexExclusive = textSpan.EndingIndexExclusive;
00203 
00204                     foreach (var textModification in textModifications)
00205                     {
00206                         if (textModification.WasInsertion)
00207                         {
00208                             if (startingIndexInclusive >= textModification.TextEditorTextSpan.StartingIndexInclusive)
00209                             {
00210                                 startingIndexInclusive += textModification.TextEditorTextSpan.Length;
00211                                 endingIndexExclusive += textModification.TextEditorTextSpan.Length;
00212                             }
00213                         }
00214                         else // was deletion
00215                         {
00216                             if (startingIndexInclusive >= textModification.TextEditorTextSpan.StartingIndexInclusive)
00217                             {
00218                                 startingIndexInclusive -= textModification.TextEditorTextSpan.Length;
00219                                 endingIndexExclusive -= textModification.TextEditorTextSpan.Length;
00220                             }
00221                         }
00222                     }
00223 
00224                     outTextSpansList.Add(textSpan with
00225                     {
00226                         StartingIndexInclusive = startingIndexInclusive,
00227                         EndingIndexExclusive = endingIndexExclusive
00228                     });
00229                 }
00230             }
00231 
00232             return outTextSpansList.ToImmutableArray();
00233         }
00234         catch (LuthetusTextEditorException)
00235         {
00236             return ImmutableArray<TextEditorTextSpan>.Empty;
00237         }
00238     }
