00248             var firstRowToSelectDataInclusive = renderBatchLocal.Model
00249                 .GetLineInformationFromPositionIndex(boundsInPositionIndexUnits.StartingIndexInclusive)
00250                 .Index;
00251 
00252             var lastRowToSelectDataExclusive = renderBatchLocal.Model
00253                 .GetLineInformationFromPositionIndex(boundsInPositionIndexUnits.EndingIndexExclusive)
00254                 .Index +
00255                 1;
00256 
00257             return (firstRowToSelectDataInclusive, lastRowToSelectDataExclusive);
