00265     public string GetTextSelectionStyleCss(
00266         int lowerPositionIndexInclusive,
00267         int upperPositionIndexExclusive,
00268         int rowIndex)
00269     {
00270         var renderBatchLocal = _renderBatch;
00271         if (renderBatchLocal is null)
00272             return "display: none;";
00273         
00274         try
00275         {
00276             if (rowIndex >= renderBatchLocal.Model.LineEndList.Count)
00277                 return string.Empty;
00278     
00279             var line = renderBatchLocal.Model.GetLineInformation(rowIndex);
00280     
00281             var selectionStartingColumnIndex = 0;
00282             var selectionEndingColumnIndex = line.EndPositionIndexExclusive - 1;
00283     
00284             var fullWidthOfRowIsSelected = true;
00285     
00286             if (lowerPositionIndexInclusive > line.StartPositionIndexInclusive)
00287             {
00288                 selectionStartingColumnIndex = lowerPositionIndexInclusive - line.StartPositionIndexInclusive;
00289                 fullWidthOfRowIsSelected = false;
00290             }
00291     
00292             if (upperPositionIndexExclusive < line.EndPositionIndexExclusive)
00293             {
00294                 selectionEndingColumnIndex = upperPositionIndexExclusive - line.StartPositionIndexInclusive;
00295                 fullWidthOfRowIsSelected = false;
00296             }
00297     
00298             var charMeasurements = renderBatchLocal.ViewModel.CharAndLineMeasurements;
00299     
00300             var topInPixelsInvariantCulture = (rowIndex * charMeasurements.LineHeight).ToCssValue();
00301             var top = $"top: {topInPixelsInvariantCulture}px;";
00302     
00303             var heightInPixelsInvariantCulture = charMeasurements.LineHeight.ToCssValue();
00304             var height = $"height: {heightInPixelsInvariantCulture}px;";
00305     
00306             var selectionStartInPixels = selectionStartingColumnIndex * charMeasurements.CharacterWidth;
00307     
00308             // selectionStartInPixels offset from Tab keys a width of many characters
00309             {
00310                 var tabsOnSameRowBeforeCursor = renderBatchLocal.Model.GetTabCountOnSameLineBeforeCursor(
00311                     rowIndex,
00312                     selectionStartingColumnIndex);
00313     
00314                 // 1 of the character width is already accounted for
00315                 var extraWidthPerTabKey = TextEditorModel.TAB_WIDTH - 1;
00316     
00317                 selectionStartInPixels += 
00318                     extraWidthPerTabKey * tabsOnSameRowBeforeCursor * charMeasurements.CharacterWidth;
00319             }
00320     
00321             var selectionStartInPixelsInvariantCulture = selectionStartInPixels.ToCssValue();
00322             var left = $"left: {selectionStartInPixelsInvariantCulture}px;";
00323     
00324             var selectionWidthInPixels = 
00325                 selectionEndingColumnIndex * charMeasurements.CharacterWidth - selectionStartInPixels;
00326     
00327             // Tab keys a width of many characters
00328             {
00329                 var lineInformation = renderBatchLocal.Model.GetLineInformation(rowIndex);
00330     
00331                 selectionEndingColumnIndex = Math.Min(
00332                     selectionEndingColumnIndex,
00333                     lineInformation.LastValidColumnIndex);
00334     
00335                 var tabsOnSameRowBeforeCursor = renderBatchLocal.Model.GetTabCountOnSameLineBeforeCursor(
00336                     rowIndex,
00337                     selectionEndingColumnIndex);
00338     
00339                 // 1 of the character width is already accounted for
00340                 var extraWidthPerTabKey = TextEditorModel.TAB_WIDTH - 1;
00341     
00342                 selectionWidthInPixels += extraWidthPerTabKey * tabsOnSameRowBeforeCursor * charMeasurements.CharacterWidth;
00343             }
00344     
00345             var widthCssStyleString = "width: ";
00346             var fullWidthValue = renderBatchLocal.ViewModel.ScrollbarDimensions.ScrollWidth;
00347     
00348             if (renderBatchLocal.ViewModel.TextEditorDimensions.Width >
00349                 renderBatchLocal.ViewModel.ScrollbarDimensions.ScrollWidth)
00350             {
00351                 // If content does not fill the viewable width of the Text Editor User Interface
00352                 fullWidthValue = renderBatchLocal.ViewModel.TextEditorDimensions.Width;
00353             }
00354     
00355             var fullWidthValueInPixelsInvariantCulture = fullWidthValue.ToCssValue();
00356     
00357             var selectionWidthInPixelsInvariantCulture = selectionWidthInPixels.ToCssValue();
00358     
00359             if (fullWidthOfRowIsSelected)
00360                 widthCssStyleString += $"{fullWidthValueInPixelsInvariantCulture}px;";
00361             else if (selectionStartingColumnIndex != 0 &&
00362                      upperPositionIndexExclusive > line.EndPositionIndexExclusive - 1)
00363                 widthCssStyleString += $"calc({fullWidthValueInPixelsInvariantCulture}px - {selectionStartInPixelsInvariantCulture}px);";
00364             else
00365                 widthCssStyleString += $"{selectionWidthInPixelsInvariantCulture}px;";
00366     
00367             return $"{top} {height} {left} {widthCssStyleString}";
00368         }
00369         catch (LuthetusTextEditorException e)
00370         {
00371             Console.WriteLine(e);
00372             return "display: none;";
00373         }
00374     }
