00075     public void ParseCodebehind()
00076     {
00077         StringWalker? stringWalker = null;
00078 
00079         if (_codebehindClassInsertions.Any())
00080             stringWalker = _codebehindClassInsertions.First().StringWalker;
00081         else if (_codebehindRenderFunctionInsertions.Any())
00082             stringWalker = _codebehindRenderFunctionInsertions.First().StringWalker;
00083 
00084         if (stringWalker is null)
00085             return;
00086 
00087         _codebehindClassBuilder.Append("\n\t");
00088 
00089         var renderFunctionAdhocTextInsertion = AdhocTextInsertion.PerformInsertion(
00090             _codebehindRenderFunctionBuilder.ToString(),
00091             0,
00092             _codebehindClassBuilder,
00093             stringWalker);
00094 
00095         foreach (var renderFunctionInsertion in _codebehindRenderFunctionInsertions)
00096         {
00097             renderFunctionInsertion.InsertionStartingIndexInclusive +=
00098                 renderFunctionAdhocTextInsertion.InsertionStartingIndexInclusive;
00099         }
00100 
00101         _codebehindClassBuilder.Append("\n\t}\n}");
00102 
00103         var classContents = _codebehindClassBuilder.ToString();
00104 
00105         var lexer = new CSharpLexer(
00106             _codebehindResourceUri,
00107             classContents);
00108 
00109         lexer.Lex();
00110 
00111         var parser = new CSharpParser(lexer);
00112 
00113         var compilationUnit = parser.Parse();
00114         
00115         SemanticResultRazor = new SemanticResultRazor(
00116             compilationUnit,
00117             _codebehindClassInsertions,
00118             _codebehindRenderFunctionInsertions,
00119             renderFunctionAdhocTextInsertion,
00120             classContents);
00121     }
