00124     public List<IHtmlSyntaxNode> ParseInjectedLanguageFragment(
00125         StringWalker stringWalker,
00126         DiagnosticBag diagnosticBag,
00127         InjectedLanguageDefinition injectedLanguageDefinition)
00128     {
00129         // current character is '@'
00130         _ = stringWalker.ReadCharacter();
00131 
00132         if (WhitespaceFacts.ALL_LIST.Contains(stringWalker.CurrentCharacter))
00133         {
00134             diagnosticBag.ReportRazorWhitespaceImmediatelyFollowingTransitionCharacterIsUnexpected(
00135                 new TextEditorTextSpan(
00136                     stringWalker.PositionIndex,
00137                     stringWalker.PositionIndex + 1,
00138                     (byte)HtmlDecorationKind.None,
00139                     stringWalker.ResourceUri,
00140                     stringWalker.SourceText));
00141 
00142             return new List<IHtmlSyntaxNode>();
00143         }
00144 
00145         // Check for both RazorKeywords and CSharpRazorKeywords
00146         {
00147             var nextWord = stringWalker.PeekNextWord();
00148 
00149             string? foundString = null;
00150 
00151             foreach (var razorKeyword in RazorKeywords.ALL)
00152             {
00153                 if (razorKeyword == nextWord)
00154                 {
00155                     foundString = razorKeyword;
00156                     break;
00157                 }
00158             }
00159 
00160             if (foundString is not null)
00161             {
00162                 return ReadRazorKeyword(
00163                     stringWalker,
00164                     diagnosticBag,
00165                     injectedLanguageDefinition,
00166                     foundString);
00167             }
00168 
00169             foreach (var cSharpRazorKeyword in CSharpRazorKeywords.ALL)
00170             {
00171                 if (cSharpRazorKeyword == nextWord)
00172                 {
00173                     foundString = cSharpRazorKeyword;
00174                     break;
00175                 }
00176             }
00177 
00178             if (foundString is not null)
00179             {
00180                 return ReadCSharpRazorKeyword(
00181                     stringWalker,
00182                     diagnosticBag,
00183                     injectedLanguageDefinition,
00184                     foundString);
00185             }
00186         }
00187 
00188         if (stringWalker.CurrentCharacter == RazorFacts.COMMENT_START)
00189         {
00190             return ReadComment(
00191                 stringWalker,
00192                 diagnosticBag,
00193                 injectedLanguageDefinition);
00194         }
00195 
00196         if (stringWalker.CurrentCharacter == RazorFacts.SINGLE_LINE_TEXT_OUTPUT_WITHOUT_ADDING_HTML_ELEMENT)
00197         {
00198             return ReadSingleLineTextOutputWithoutAddingHtmlElement(
00199                 stringWalker,
00200                 diagnosticBag,
00201                 injectedLanguageDefinition);
00202         }
00203 
00204         if (stringWalker.CurrentCharacter == RazorFacts.CODE_BLOCK_START)
00205         {
00206             return ReadCodeBlock(
00207                 stringWalker,
00208                 diagnosticBag,
00209                 injectedLanguageDefinition,
00210                 false);
00211         }
00212 
00213         // TODO: Check for invalid expressions
00214         return ReadInlineExpression(
00215             stringWalker,
00216             diagnosticBag,
00217             injectedLanguageDefinition);
00218     }
