00220     public void ParseTagName(
00221         StringWalker stringWalker,
00222         DiagnosticBag diagnosticBag,
00223         InjectedLanguageDefinition injectedLanguageDefinition,
00224         TextEditorTextSpan textSpan)
00225     {
00226         var allTypeDefinitions = _cSharpCompilerService.CSharpBinder.AllTypeDefinitions;
00227 
00228         var text = textSpan.GetText();
00229         
00230         var matchingKvps = allTypeDefinitions.Where(x => x.Key.TypeIdentifier == text);
00231 
00232         foreach (var kvp in matchingKvps)
00233         {
00234             var typeDefinitionNode = kvp.Value;
00235             
00236             if (typeDefinitionNode?.InheritedTypeClauseNode is not null)
00237             {
00238                 var inheritanceIdentifierText = typeDefinitionNode
00239                     .InheritedTypeClauseNode.TypeIdentifierToken.TextSpan.GetText();
00240 
00241                 if (inheritanceIdentifierText != "ComponentBase")
00242                     continue;
00243 
00244                 var compilerServiceResource = _razorCompilerService.GetCompilerServiceResourceFor(textSpan.ResourceUri);
00245 
00246                 if (compilerServiceResource is null)
00247                     continue;
00248 
00249                 var razorResource = (RazorResource)compilerServiceResource;
00250 
00251                 razorResource.HtmlSymbols.Add(new InjectedLanguageComponentSymbol(
00252                     textSpan));
00253             }
00254         }
00255     }
