00257     public static AttributeNameNode ParseAttributeName(
00258         StringWalker stringWalker,
00259         DiagnosticBag diagnosticBag,
00260         InjectedLanguageDefinition injectedLanguageDefinition)
00261     {
00262         var startingPositionIndex = stringWalker.PositionIndex;
00263 
00264         while (!stringWalker.IsEof)
00265         {
00266             _ = stringWalker.ReadCharacter();
00267 
00268             if (WhitespaceFacts.ALL_LIST.Contains(stringWalker.CurrentCharacter) ||
00269                 HtmlFacts.SEPARATOR_FOR_ATTRIBUTE_NAME_AND_ATTRIBUTE_VALUE == stringWalker.CurrentCharacter ||
00270                 stringWalker.PeekForSubstringRange(HtmlFacts.OPEN_TAG_ENDING_OPTIONS, out var matchedOn))
00271             {
00272                 break;
00273             }
00274         }
00275 
00276         var attributeNameTextSpan = new TextEditorTextSpan(
00277             startingPositionIndex,
00278             stringWalker.PositionIndex,
00279             (byte)HtmlDecorationKind.InjectedLanguageFragment,
00280             stringWalker.ResourceUri,
00281             stringWalker.SourceText);
00282 
00283         return new AttributeNameNode(attributeNameTextSpan);
00284     }
