00295     private List<IHtmlSyntaxNode> ReadCodeBlock(
00296         StringWalker stringWalker,
00297         DiagnosticBag diagnosticBag,
00298         InjectedLanguageDefinition injectedLanguageDefinition,
00299         bool isClassLevelCodeBlock)
00300     {
00301         var injectedLanguageFragmentSyntaxes = new List<IHtmlSyntaxNode>();
00302 
00303         var startingPositionIndex = stringWalker.PositionIndex;
00304 
00305         // Syntax highlight the CODE_BLOCK_START as a razor keyword specifically
00306         {
00307             injectedLanguageFragmentSyntaxes.Add(
00308                 new InjectedLanguageFragmentNode(
00309                     ImmutableArray<IHtmlSyntax>.Empty,
00310                     new TextEditorTextSpan(
00311                         stringWalker.PositionIndex,
00312                         stringWalker.PositionIndex +
00313                         1,
00314                         (byte)HtmlDecorationKind.InjectedLanguageFragment,
00315                         stringWalker.ResourceUri,
00316                         stringWalker.SourceText)));
00317         }
00318 
00319         // Enters the while loop on the '{'
00320         var unmatchedCodeBlockStarts = 1;
00321 
00322         // While iterating through the text append any C# text to the cSharpBuilder afterwards pass it through Roslyn for the syntax highlighting and map the corresponding position indices.
00323         var cSharpBuilder = new StringBuilder();
00324 
00325         var positionIndexOffset = stringWalker.PositionIndex + 1;
00326 
00327         while (!stringWalker.IsEof)
00328         {
00329             _ = stringWalker.ReadCharacter();
00330 
00331             if (!isClassLevelCodeBlock)
00332             {
00333                 if (stringWalker.CurrentCharacter == HtmlFacts.OPEN_TAG_BEGINNING)
00334                 {
00335                     var positionIndexPriorToHtmlTag = stringWalker.PositionIndex;
00336 
00337                     var tagSyntax = HtmlSyntaxTree.HtmlSyntaxTreeStateMachine.ParseTag(
00338                         stringWalker,
00339                         diagnosticBag,
00340                         injectedLanguageDefinition);
00341 
00342                     injectedLanguageFragmentSyntaxes.Add(tagSyntax);
00343 
00344                     var necessaryWhitespacePadding =
00345                         stringWalker.PositionIndex -
00346                         positionIndexPriorToHtmlTag +
00347                         1;
00348 
00349                     for (int i = 0; i < necessaryWhitespacePadding; i++)
00350                     {
00351                         cSharpBuilder.Append(WhitespaceFacts.SPACE);
00352                     }
00353 
00354                     continue;
00355                 }
00356 
00357                 // '@:' is a single line version of '<text></text>' as of this comment | 2022-12-07
00358                 var singleLineTextOutputText =
00359                     $"{RazorFacts.TRANSITION_SUBSTRING}{RazorFacts.SINGLE_LINE_TEXT_OUTPUT_WITHOUT_ADDING_HTML_ELEMENT}";
00360 
00361                 if (stringWalker.PeekForSubstring(singleLineTextOutputText))
00362                 {
00363                     var positionIndexPriorReadingLine = stringWalker.PositionIndex;
00364 
00365                     var injectedLanguageFragmentSyntaxStartingPositionIndex =
00366                         stringWalker.PositionIndex;
00367 
00368                     // Track text span of the "@" sign
00369                     injectedLanguageFragmentSyntaxes.Add(
00370                         new InjectedLanguageFragmentNode(
00371                             ImmutableArray<IHtmlSyntax>.Empty,
00372                             new TextEditorTextSpan(
00373                                 injectedLanguageFragmentSyntaxStartingPositionIndex,
00374                                 stringWalker.PositionIndex + 1,
00375                                 (byte)HtmlDecorationKind.InjectedLanguageFragment,
00376                                 stringWalker.ResourceUri,
00377                                 stringWalker.SourceText)));
00378 
00379                     // Move beyond the "@" sign
00380                     _ = stringWalker.ReadCharacter();
00381 
00382                     injectedLanguageFragmentSyntaxes.AddRange(ReadSingleLineTextOutputWithoutAddingHtmlElement(
00383                         stringWalker,
00384                         diagnosticBag,
00385                         injectedLanguageDefinition));
00386 
00387                     var necessaryWhitespacePadding =
00388                         stringWalker.PositionIndex -
00389                         positionIndexPriorReadingLine +
00390                         1;
00391 
00392                     for (int i = 0; i < necessaryWhitespacePadding; i++)
00393                     {
00394                         cSharpBuilder.Append(WhitespaceFacts.SPACE);
00395                     }
00396 
00397                     continue;
00398                 }
00399             }
00400 
00401             // Track all the C# text
00402             cSharpBuilder.Append(stringWalker.CurrentCharacter);
00403 
00404             if (stringWalker.CurrentCharacter == RazorFacts.CODE_BLOCK_START)
00405             {
00406                 unmatchedCodeBlockStarts++;
00407                 continue;
00408             }
00409 
00410             if (stringWalker.CurrentCharacter == RazorFacts.CODE_BLOCK_END)
00411             {
00412                 unmatchedCodeBlockStarts--;
00413 
00414                 if (unmatchedCodeBlockStarts == 0)
00415                 {
00416                     // Syntax highlight the CODE_BLOCK_END as a razor keyword specifically
00417                     {
00418                         injectedLanguageFragmentSyntaxes.Add(
00419                             new InjectedLanguageFragmentNode(
00420                                 ImmutableArray<IHtmlSyntax>.Empty,
00421                                 new TextEditorTextSpan(
00422                                     stringWalker.PositionIndex,
00423                                     stringWalker.PositionIndex +
00424                                     1,
00425                                     (byte)HtmlDecorationKind.InjectedLanguageFragment,
00426                                     stringWalker.ResourceUri,
00427                                     stringWalker.SourceText)));
00428                     }
00429 
00430                     // A final '}' will be erroneously appended so remove that
00431                     cSharpBuilder.Remove(cSharpBuilder.Length - 1, 1);
00432 
00433                     var cSharpText = cSharpBuilder.ToString();
00434 
00435                     if (isClassLevelCodeBlock)
00436                     {
00437                         injectedLanguageFragmentSyntaxes.AddRange(
00438                             ParseCSharpWithAdhocClassWrapping(
00439                                 cSharpText,
00440                                 positionIndexOffset,
00441                                 stringWalker));
00442                     }
00443                     else
00444                     {
00445                         injectedLanguageFragmentSyntaxes.AddRange(
00446                             ParseCSharpWithAdhocMethodWrapping(
00447                                 cSharpText,
00448                                 positionIndexOffset,
00449                                 stringWalker));
00450                     }
00451 
00452                     break;
00453                 }
00454             }
00455         }
00456 
00457         return injectedLanguageFragmentSyntaxes;
00458     }
