00480     private List<IHtmlSyntaxNode> ReadExplicitInlineExpression(
00481         StringWalker stringWalker,
00482         DiagnosticBag diagnosticBag,
00483         InjectedLanguageDefinition injectedLanguageDefinition)
00484     {
00485         var injectedLanguageFragmentSyntaxes = new List<IHtmlSyntaxNode>();
00486 
00487         var startingPositionIndex = stringWalker.PositionIndex;
00488 
00489         // Syntax highlight the EXPLICIT_EXPRESSION_START as a razor keyword specifically
00490         {
00491             injectedLanguageFragmentSyntaxes.Add(
00492                 new InjectedLanguageFragmentNode(
00493                     ImmutableArray<IHtmlSyntax>.Empty,
00494                     new TextEditorTextSpan(
00495                         stringWalker.PositionIndex,
00496                         stringWalker.PositionIndex +
00497                         1,
00498                         (byte)HtmlDecorationKind.InjectedLanguageFragment,
00499                         stringWalker.ResourceUri,
00500                         stringWalker.SourceText)));
00501         }
00502 
00503         // Enters the while loop on the '('
00504         var unmatchedExplicitExpressionStarts = 1;
00505 
00506         while (!stringWalker.IsEof)
00507         {
00508             _ = stringWalker.ReadCharacter();
00509 
00510             if (stringWalker.CurrentCharacter == RazorFacts.EXPLICIT_EXPRESSION_START)
00511                 unmatchedExplicitExpressionStarts++;
00512 
00513             if (stringWalker.CurrentCharacter == RazorFacts.EXPLICIT_EXPRESSION_END)
00514             {
00515                 unmatchedExplicitExpressionStarts--;
00516 
00517                 if (unmatchedExplicitExpressionStarts == 0)
00518                 {
00519                     // Syntax highlight the EXPLICIT_EXPRESSION_END as a razor keyword specifically
00520                     {
00521                         injectedLanguageFragmentSyntaxes.Add(
00522                             new InjectedLanguageFragmentNode(
00523                                 ImmutableArray<IHtmlSyntax>.Empty,
00524                                 new TextEditorTextSpan(
00525                                     stringWalker.PositionIndex,
00526                                     stringWalker.PositionIndex +
00527                                     1,
00528                                     (byte)HtmlDecorationKind.InjectedLanguageFragment,
00529                                     stringWalker.ResourceUri,
00530                                     stringWalker.SourceText)));
00531                     }
00532 
00533                     break;
00534                 }
00535             }
00536         }
00537 
00538         // TODO: Syntax highlighting
00539         return injectedLanguageFragmentSyntaxes;
00540     }
