00579     private List<IHtmlSyntaxNode> ReadCSharpRazorKeyword(
00580         StringWalker stringWalker,
00581         DiagnosticBag diagnosticBag,
00582         InjectedLanguageDefinition injectedLanguageDefinition,
00583         string matchedOn)
00584     {
00585         var injectedLanguageFragmentSyntaxes = new List<IHtmlSyntaxNode>();
00586 
00587         // Syntax highlight the keyword as a razor keyword specifically
00588         {
00589             injectedLanguageFragmentSyntaxes.Add(
00590                 new InjectedLanguageFragmentNode(
00591                     ImmutableArray<IHtmlSyntax>.Empty,
00592                     new TextEditorTextSpan(
00593                         stringWalker.PositionIndex,
00594                         stringWalker.PositionIndex +
00595                         matchedOn.Length,
00596                         (byte)HtmlDecorationKind.InjectedLanguageFragment,
00597                         stringWalker.ResourceUri,
00598                         stringWalker.SourceText)));
00599 
00600             _ = stringWalker
00601                 .ReadRange(matchedOn.Length);
00602         }
00603 
00604         switch (matchedOn)
00605         {
00606             case CSharpRazorKeywords.CASE_KEYWORD:
00607                 break;
00608             case CSharpRazorKeywords.DO_KEYWORD:
00609                 {
00610                     // Necessary in the case where the do-while statement's code block immediately follows the 'do' text. Example: "@do{"
00611                     stringWalker.BacktrackCharacter();
00612 
00613                     if (!TryReadCodeBlock(
00614                             stringWalker,
00615                             diagnosticBag,
00616                             injectedLanguageDefinition,
00617                             CSharpRazorKeywords.DO_KEYWORD,
00618                             out var codeBlockTagSyntaxes) ||
00619                         codeBlockTagSyntaxes is null)
00620                     {
00621                         break;
00622                     }
00623 
00624                     injectedLanguageFragmentSyntaxes.AddRange(codeBlockTagSyntaxes);
00625 
00626                     if (TryReadWhileOfDoWhile(
00627                             stringWalker,
00628                             diagnosticBag,
00629                             injectedLanguageDefinition,
00630                             CSharpRazorKeywords.DO_KEYWORD,
00631                             out var whileOfDoWhileTagSyntaxes) &&
00632                         whileOfDoWhileTagSyntaxes is not null)
00633                     {
00634                         injectedLanguageFragmentSyntaxes.AddRange(whileOfDoWhileTagSyntaxes);
00635                     }
00636 
00637                     break;
00638                 }
00639             case CSharpRazorKeywords.DEFAULT_KEYWORD:
00640                 break;
00641             case CSharpRazorKeywords.FOR_KEYWORD:
00642                 {
00643                     // Necessary in the case where the switch statement's predicate expression immediately follows the 'switch' text. Example: "@switch(predicate) {"
00644                     stringWalker.BacktrackCharacter();
00645 
00646                     if (!TryReadExplicitInlineExpression(
00647                             stringWalker,
00648                             diagnosticBag,
00649                             injectedLanguageDefinition,
00650                             CSharpRazorKeywords.FOR_KEYWORD,
00651                             out var explicitExpressionTagSyntaxes) ||
00652                         explicitExpressionTagSyntaxes is null)
00653                     {
00654                         break;
00655                     }
00656 
00657                     injectedLanguageFragmentSyntaxes.AddRange(explicitExpressionTagSyntaxes);
00658 
00659                     if (TryReadCodeBlock(
00660                             stringWalker,
00661                             diagnosticBag,
00662                             injectedLanguageDefinition,
00663                             CSharpRazorKeywords.FOR_KEYWORD,
00664                             out var codeBlockTagSyntaxes) &&
00665                         codeBlockTagSyntaxes is not null)
00666                     {
00667                         injectedLanguageFragmentSyntaxes.AddRange(codeBlockTagSyntaxes);
00668                     }
00669 
00670                     break;
00671                 }
00672             case CSharpRazorKeywords.FOREACH_KEYWORD:
00673                 {
00674                     // Necessary in the case where the foreach statement's predicate expression immediately follows the 'foreach' text. Example: "@foreach(predicate) {"
00675                     stringWalker.BacktrackCharacter();
00676 
00677                     if (!TryReadExplicitInlineExpression(
00678                             stringWalker,
00679                             diagnosticBag,
00680                             injectedLanguageDefinition,
00681                             CSharpRazorKeywords.FOREACH_KEYWORD,
00682                             out var explicitExpressionTagSyntaxes) ||
00683                         explicitExpressionTagSyntaxes is null)
00684                     {
00685                         break;
00686                     }
00687 
00688                     injectedLanguageFragmentSyntaxes.AddRange(explicitExpressionTagSyntaxes);
00689 
00690                     if (TryReadCodeBlock(
00691                             stringWalker,
00692                             diagnosticBag,
00693                             injectedLanguageDefinition,
00694                             CSharpRazorKeywords.FOREACH_KEYWORD,
00695                             out var codeBlockTagSyntaxes) &&
00696                         codeBlockTagSyntaxes is not null)
00697                     {
00698                         injectedLanguageFragmentSyntaxes.AddRange(codeBlockTagSyntaxes);
00699                     }
00700 
00701                     break;
00702                 }
00703             case CSharpRazorKeywords.IF_KEYWORD:
00704                 {
00705                     // Necessary in the case where the if statement's predicate expression immediately follows the 'if' text. Example: "@if(predicate) {"
00706                     stringWalker.BacktrackCharacter();
00707 
00708                     if (!TryReadExplicitInlineExpression(
00709                             stringWalker,
00710                             diagnosticBag,
00711                             injectedLanguageDefinition,
00712                             CSharpRazorKeywords.IF_KEYWORD,
00713                             out var explicitExpressionTagSyntaxes) ||
00714                         explicitExpressionTagSyntaxes is null)
00715                     {
00716                         break;
00717                     }
00718 
00719                     injectedLanguageFragmentSyntaxes.AddRange(explicitExpressionTagSyntaxes);
00720 
00721                     if (TryReadCodeBlock(
00722                             stringWalker,
00723                             diagnosticBag,
00724                             injectedLanguageDefinition,
00725                             CSharpRazorKeywords.IF_KEYWORD,
00726                             out var codeBlockTagSyntaxes) &&
00727                         codeBlockTagSyntaxes is not null)
00728                     {
00729                         injectedLanguageFragmentSyntaxes.AddRange(codeBlockTagSyntaxes);
00730                     }
00731 
00732                     var restorePositionIndexPriorToTryReadElseIf = stringWalker.PositionIndex;
00733 
00734                     while (TryReadElseIf(
00735                                stringWalker,
00736                                diagnosticBag,
00737                                injectedLanguageDefinition,
00738                                CSharpRazorKeywords.IF_KEYWORD,
00739                                out var elseIfTagSyntaxes) &&
00740                            elseIfTagSyntaxes is not null)
00741                     {
00742                         injectedLanguageFragmentSyntaxes.AddRange(elseIfTagSyntaxes);
00743                         restorePositionIndexPriorToTryReadElseIf = stringWalker.PositionIndex;
00744                     }
00745 
00746                     if (restorePositionIndexPriorToTryReadElseIf != stringWalker.PositionIndex)
00747                     {
00748                         stringWalker.BacktrackRange(
00749                             stringWalker.PositionIndex - restorePositionIndexPriorToTryReadElseIf);
00750                     }
00751 
00752                     if (TryReadElse(
00753                             stringWalker,
00754                             diagnosticBag,
00755                             injectedLanguageDefinition,
00756                             CSharpRazorKeywords.IF_KEYWORD,
00757                             out var elseTagSyntaxes) &&
00758                         elseTagSyntaxes is not null)
00759                     {
00760                         injectedLanguageFragmentSyntaxes.AddRange(elseTagSyntaxes);
00761                     }
00762 
00763                     break;
00764                 }
00765             case CSharpRazorKeywords.ELSE_KEYWORD:
00766                 break;
00767             case CSharpRazorKeywords.LOCK_KEYWORD:
00768                 break;
00769             case CSharpRazorKeywords.SWITCH_KEYWORD:
00770                 {
00771                     // Necessary in the case where the switch statement's predicate expression immediately follows the 'switch' text. Example: "@switch(predicate) {"
00772                     stringWalker.BacktrackCharacter();
00773 
00774                     if (!TryReadExplicitInlineExpression(
00775                             stringWalker,
00776                             diagnosticBag,
00777                             injectedLanguageDefinition,
00778                             CSharpRazorKeywords.SWITCH_KEYWORD,
00779                             out var explicitExpressionTagSyntaxes) ||
00780                         explicitExpressionTagSyntaxes is null)
00781                     {
00782                         break;
00783                     }
00784 
00785                     injectedLanguageFragmentSyntaxes.AddRange(explicitExpressionTagSyntaxes);
00786 
00787                     if (TryReadCodeBlock(
00788                             stringWalker,
00789                             diagnosticBag,
00790                             injectedLanguageDefinition,
00791                             CSharpRazorKeywords.SWITCH_KEYWORD,
00792                             out var codeBlockTagSyntaxes) &&
00793                         codeBlockTagSyntaxes is not null)
00794                     {
00795                         injectedLanguageFragmentSyntaxes.AddRange(codeBlockTagSyntaxes);
00796                     }
00797 
00798                     break;
00799                 }
00800             case CSharpRazorKeywords.TRY_KEYWORD:
00801                 break;
00802             case CSharpRazorKeywords.CATCH_KEYWORD:
00803                 break;
00804             case CSharpRazorKeywords.FINALLY_KEYWORD:
00805                 break;
00806             case CSharpRazorKeywords.USING_KEYWORD:
00807                 break;
00808             case CSharpRazorKeywords.WHILE_KEYWORD:
00809                 {
00810                     // Necessary in the case where the while statement's predicate expression immediately follows the 'while' text. Example: "@while(predicate) {"
00811                     stringWalker.BacktrackCharacter();
00812 
00813                     if (!TryReadExplicitInlineExpression(
00814                             stringWalker,
00815                             diagnosticBag,
00816                             injectedLanguageDefinition,
00817                             CSharpRazorKeywords.IF_KEYWORD,
00818                             out var explicitExpressionTagSyntaxes) ||
00819                         explicitExpressionTagSyntaxes is null)
00820                     {
00821                         break;
00822                     }
00823 
00824                     injectedLanguageFragmentSyntaxes.AddRange(explicitExpressionTagSyntaxes);
00825 
00826                     if (TryReadCodeBlock(
00827                             stringWalker,
00828                             diagnosticBag,
00829                             injectedLanguageDefinition,
00830                             CSharpRazorKeywords.IF_KEYWORD,
00831                             out var codeBlockTagSyntaxes) &&
00832                         codeBlockTagSyntaxes is not null)
00833                     {
00834                         injectedLanguageFragmentSyntaxes.AddRange(codeBlockTagSyntaxes);
00835                     }
00836 
00837                     break;
00838                 }
00839         }
00840 
00841         return injectedLanguageFragmentSyntaxes;
00842     }
00843 
00845     private List<IHtmlSyntaxNode> ReadRazorKeyword(
00846         StringWalker stringWalker,
00847         DiagnosticBag diagnosticBag,
00848         InjectedLanguageDefinition injectedLanguageDefinition,
00849         string matchedOn)
00850     {
00851         var injectedLanguageFragmentSyntaxes = new List<IHtmlSyntaxNode>();
00852 
00853         // Syntax highlight the keyword as a razor keyword specifically
00854         {
00855             injectedLanguageFragmentSyntaxes.Add(
00856                 new InjectedLanguageFragmentNode(
00857                     ImmutableArray<IHtmlSyntax>.Empty,
00858                     new TextEditorTextSpan(
00859                         stringWalker.PositionIndex,
00860                         stringWalker.PositionIndex +
00861                         matchedOn.Length,
00862                         (byte)HtmlDecorationKind.InjectedLanguageFragment,
00863                         stringWalker.ResourceUri,
00864                         stringWalker.SourceText)));
00865 
00866             _ = stringWalker
00867                 .ReadRange(matchedOn.Length);
00868         }
00869 
00870         switch (matchedOn)
00871         {
00872             case RazorKeywords.PAGE_KEYWORD:
00873                 {
00874                     // @page "/csharp"
00875                     break;
00876                 }
00877             case RazorKeywords.NAMESPACE_KEYWORD:
00878                 {
00879                     // @namespace Luthetus.TextEditor.Demo.ServerSide.Pages
00880                     break;
00881                 }
00882             case RazorKeywords.CODE_KEYWORD:
00883             case RazorKeywords.FUNCTIONS_KEYWORD:
00884                 {
00885                     // Necessary in the case where the code block immediately follows any keyword's text. Example: "@code{" 
00886                     stringWalker.BacktrackCharacter();
00887 
00888                     var keywordText = matchedOn == RazorKeywords.CODE_KEYWORD
00889                         ? RazorKeywords.CODE_KEYWORD
00890                         : RazorKeywords.FUNCTIONS_KEYWORD;
00891 
00892                     if (TryReadCodeBlock(
00893                             stringWalker,
00894                             diagnosticBag,
00895                             injectedLanguageDefinition,
00896                             keywordText,
00897                             out var codeBlockTagSyntaxes) &&
00898                         codeBlockTagSyntaxes is not null)
00899                     {
00900                         return injectedLanguageFragmentSyntaxes
00901                             .Union(codeBlockTagSyntaxes)
00902                             .ToList();
00903                     }
00904 
00905                     break;
00906                 }
00907             case RazorKeywords.INHERITS_KEYWORD:
00908                 {
00909                     // @inherits IconBase
00910                     break;
00911                 }
00912         }
00913 
00914         return injectedLanguageFragmentSyntaxes;
00915     }
