00918     private List<IHtmlSyntaxNode> ReadComment(
00919         StringWalker stringWalker,
00920         DiagnosticBag diagnosticBag,
00921         InjectedLanguageDefinition injectedLanguageDefinition)
00922     {
00923         var injectedLanguageFragmentSyntaxes = new List<IHtmlSyntaxNode>();
00924 
00925         // Enters the while loop on the '*'
00926 
00927         // Syntax highlight the '*' the same color as a razor keyword
00928         {
00929             var commentStartTextSpan = new TextEditorTextSpan(
00930                 stringWalker.PositionIndex,
00931                 stringWalker.PositionIndex + 1,
00932                 (byte)HtmlDecorationKind.InjectedLanguageFragment,
00933                 stringWalker.ResourceUri,
00934                 stringWalker.SourceText);
00935 
00936             var commentStartSyntax = new InjectedLanguageFragmentNode(
00937                 ImmutableArray<IHtmlSyntax>.Empty,
00938                 commentStartTextSpan);
00939 
00940             injectedLanguageFragmentSyntaxes.Add(commentStartSyntax);
00941         }
00942 
00943         // Do not syntax highlight the '*' as part of the comment
00944         var commentTextStartingPositionIndex = stringWalker.PositionIndex + 1;
00945 
00946         while (!stringWalker.IsEof)
00947         {
00948             _ = stringWalker.ReadCharacter();
00949 
00950             if (stringWalker.CurrentCharacter == RazorFacts.COMMENT_END &&
00951                 stringWalker.NextCharacter.ToString() == RazorFacts.TRANSITION_SUBSTRING)
00952             {
00953                 break;
00954             }
00955         }
00956 
00957         var commentValueTextSpan = new TextEditorTextSpan(
00958                 commentTextStartingPositionIndex,
00959                 stringWalker.PositionIndex,
00960                 (byte)HtmlDecorationKind.Comment,
00961                 stringWalker.ResourceUri,
00962                 stringWalker.SourceText);
00963 
00964         var commentValueSyntax = new InjectedLanguageFragmentNode(
00965             ImmutableArray<IHtmlSyntax>.Empty,
00966             commentValueTextSpan);
00967 
00968         injectedLanguageFragmentSyntaxes.Add(commentValueSyntax);
00969 
00970         // Syntax highlight the '*' the same color as a razor keyword
00971         {
00972             var commentEndTextSpan = new TextEditorTextSpan(
00973                 stringWalker.PositionIndex,
00974                 stringWalker.PositionIndex + 1,
00975                 (byte)HtmlDecorationKind.InjectedLanguageFragment,
00976                 stringWalker.ResourceUri,
00977                 stringWalker.SourceText);
00978 
00979             var commentEndSyntax = new InjectedLanguageFragmentNode(
00980                 ImmutableArray<IHtmlSyntax>.Empty,
00981                 commentEndTextSpan);
00982 
00983             injectedLanguageFragmentSyntaxes.Add(commentEndSyntax);
00984         }
00985 
00986         return injectedLanguageFragmentSyntaxes;
00987     }
