00990     private List<IHtmlSyntaxNode> ReadSingleLineTextOutputWithoutAddingHtmlElement(
00991         StringWalker stringWalker,
00992         DiagnosticBag diagnosticBag,
00993         InjectedLanguageDefinition injectedLanguageDefinition)
00994     {
00995         var injectedLanguageFragmentSyntaxes = new List<IHtmlSyntaxNode>();
00996 
00997         // Enters the while loop on the ':'
00998 
00999         // Syntax highlight the ':' the same color as a razor keyword
01000         {
01001             var commentStartTextSpan = new TextEditorTextSpan(
01002                 stringWalker.PositionIndex,
01003                 stringWalker.PositionIndex + 1,
