01028     private bool TryReadCodeBlock(
01029         StringWalker stringWalker,
01030         DiagnosticBag diagnosticBag,
01031         InjectedLanguageDefinition injectedLanguageDefinition,
01032         string keywordText,
01033         out List<IHtmlSyntaxNode>? tagSyntaxes)
01034     {
01035         while (!stringWalker.IsEof)
01036         {
01037             _ = stringWalker.ReadCharacter();
01038 
01039             if (stringWalker.CurrentCharacter == RazorFacts.CODE_BLOCK_START)
01040             {
01041                 var isClassLevelCodeBlock =
01042                     keywordText == RazorKeywords.CODE_KEYWORD ||
01043                     keywordText == RazorKeywords.FUNCTIONS_KEYWORD;
01044 
01045                 var codeBlockTagSyntaxes = ReadCodeBlock(
01046                     stringWalker,
01047                     diagnosticBag,
01048                     injectedLanguageDefinition,
01049                     isClassLevelCodeBlock);
01050 
01051                 tagSyntaxes = codeBlockTagSyntaxes;
01052                 return true;
01053             }
01054 
01055             if (WhitespaceFacts.ALL_LIST.Contains(stringWalker.CurrentCharacter))
01056                 continue;
01057 
01058             diagnosticBag.ReportRazorCodeBlockWasExpectedToFollowRazorKeyword(
01059                 RazorFacts.TRANSITION_SUBSTRING,
01060                 keywordText,
01061                 new TextEditorTextSpan(
01062                     stringWalker.PositionIndex,
01063                     stringWalker.PositionIndex + 1,
01064                     (byte)HtmlDecorationKind.None,
01065                     stringWalker.ResourceUri,
01066                     stringWalker.SourceText));
01067 
01068             break;
01069         }
01070 
01071         tagSyntaxes = null;
01072         return false;
01073     }
01074 
01075     private bool TryReadExplicitInlineExpression(
01076         StringWalker stringWalker,
01077         DiagnosticBag diagnosticBag,
01078         InjectedLanguageDefinition injectedLanguageDefinition,
01079         string keywordText,
01080         out List<IHtmlSyntaxNode>? tagSyntaxes)
01081     {
01082         while (!stringWalker.IsEof)
01083         {
01084             _ = stringWalker.ReadCharacter();
01085 
01086             if (stringWalker.CurrentCharacter == RazorFacts.EXPLICIT_EXPRESSION_START)
01087             {
01088                 var explicitExpressionTagSyntaxes = ReadExplicitInlineExpression(
01089                     stringWalker,
01090                     diagnosticBag,
01091                     injectedLanguageDefinition);
01092 
01093                 tagSyntaxes = explicitExpressionTagSyntaxes;
01094                 return true;
01095             }
01096 
01097             if (WhitespaceFacts.ALL_LIST.Contains(stringWalker.CurrentCharacter))
01098                 continue;
01099 
01100             diagnosticBag.ReportRazorExplicitExpressionPredicateWasExpected(
01101                 RazorFacts.TRANSITION_SUBSTRING,
01102                 keywordText,
01103                 new TextEditorTextSpan(
01104                     stringWalker.PositionIndex,
01105                     stringWalker.PositionIndex + 1,
01106                     (byte)HtmlDecorationKind.None,
01107                     stringWalker.ResourceUri,
01108                     stringWalker.SourceText));
01109 
01110             break;
01111         }
01112 
01113         tagSyntaxes = null;
01114         return false;
01115     }
