01117     private bool TryReadElseIf(
01118         StringWalker stringWalker,
01119         DiagnosticBag diagnosticBag,
01120         InjectedLanguageDefinition injectedLanguageDefinition,
01121         string keywordText,
01122         out List<IHtmlSyntaxNode>? tagSyntaxes)
01123     {
01124         tagSyntaxes = new List<IHtmlSyntaxNode>();
01125 
01126         while (!stringWalker.IsEof)
01127         {
01128             _ = stringWalker.ReadCharacter();
01129 
01130             var elseIfKeywordCombo =
01131                 $"{CSharpRazorKeywords.ELSE_KEYWORD} {CSharpRazorKeywords.IF_KEYWORD}";
01132 
01133             if (stringWalker.PeekForSubstring(elseIfKeywordCombo))
01134             {
01135                 // Syntax highlight the keyword as a razor keyword specifically
01136                 {
01137                     tagSyntaxes.Add(
01138                         new InjectedLanguageFragmentNode(
01139                             ImmutableArray<IHtmlSyntax>.Empty,
01140                             new TextEditorTextSpan(
01141                                 stringWalker.PositionIndex,
01142                                 stringWalker.PositionIndex +
01143                                 elseIfKeywordCombo.Length,
01144                                 (byte)HtmlDecorationKind.InjectedLanguageFragment,
01145                                 stringWalker.ResourceUri,
01146                                 stringWalker.SourceText)));
01147 
01148                     // -1 is in the case that "else{" instead of a space between "else" and "{"
01149                     _ = stringWalker
01150                         .ReadRange(elseIfKeywordCombo.Length - 1);
01151                 }
01152 
01153                 if (!TryReadExplicitInlineExpression(
01154                         stringWalker,
01155                         diagnosticBag,
01156                         injectedLanguageDefinition,
01157                         CSharpRazorKeywords.IF_KEYWORD,
01158                         out var explicitExpressionTagSyntaxes) ||
01159                     explicitExpressionTagSyntaxes is null)
01160                 {
01161                     break;
01162                 }
01163 
01164                 tagSyntaxes.AddRange(explicitExpressionTagSyntaxes);
01165 
01166                 if (TryReadCodeBlock(
01167                         stringWalker,
01168                         diagnosticBag,
01169                         injectedLanguageDefinition,
01170                         CSharpRazorKeywords.ELSE_KEYWORD,
01171                         out var codeBlockTagSyntaxes) &&
01172                     codeBlockTagSyntaxes is not null)
01173                 {
01174                     tagSyntaxes.AddRange(codeBlockTagSyntaxes);
01175                     return true;
01176                 }
01177 
01178                 break;
01179             }
01180 
01181             if (WhitespaceFacts.ALL_LIST.Contains(stringWalker.CurrentCharacter))
01182                 continue;
01183 
01184             break;
01185         }
01186 
01187         tagSyntaxes = tagSyntaxes.Any()
01188             ? tagSyntaxes
01189             : null;
01190 
01191         return false;
01192     }
